import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Dimensions,
  Animated,
  Image,
} from 'react-native';

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

// Enhanced noise component for glass texture effect
const NoiseOverlay = () => {
  const noiseDots = Array.from({ length: 600 }, (_, i) => (
    <View
      key={i}
      style={[
        styles.noiseDot,
        {
          left: Math.random() * 100 + '%',
          top: Math.random() * 100 + '%',
          opacity: Math.random() * 0.4 + 0.05,
          width: Math.random() * 1.5 + 0.3,
          height: Math.random() * 1.5 + 0.3,
          backgroundColor: Math.random() > 0.5 
            ? 'rgba(255, 255, 255, 0.1)' 
            : 'rgba(0, 0, 0, 0.1)',
        },
      ]}
    />
  ));

  return <View style={styles.noiseContainer}>{noiseDots}</View>;
};

const SideMenu = ({ isVisible, onClose }) => {
  const slideAnim = React.useRef(new Animated.Value(screenWidth)).current;

  React.useEffect(() => {
    if (isVisible) {
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 300,
        useNativeDriver: true,
      }).start();
    } else {
      Animated.timing(slideAnim, {
        toValue: screenWidth,
        duration: 300,
        useNativeDriver: true,
      }).start();
    }
  }, [isVisible, slideAnim]);

  if (!isVisible) return null;

  const menuItems = [
    { id: 'perfil', title: 'Perfil', y: 200, height: 42 },
    { id: 'ranking', title: 'Ranking', y: 269, height: 42 },
    { id: 'chat', title: 'Chat', y: 339, height: 41 },
    { id: 'tutorial', title: 'Tutorial', y: 408, height: 42 },
    { id: 'menu', title: 'Menu', y: 477, height: 42 },
  ];

  return (
    <View style={styles.overlay}>
      <TouchableOpacity style={styles.backdrop} onPress={onClose} />
      <Animated.View
        style={[
          styles.menuContainer,
          {
            transform: [{ translateX: slideAnim }],
          },
        ]}
      >
        {/* Background Rectangle 44 - Main container */}
        <View style={styles.backgroundRect}>
          {/* Base gradient color - Start */}
          <View style={styles.gradientBase} />
          {/* Gradient transition layer */}
          <View style={styles.gradientTransition} />
          {/* Gradient end color */}
          <View style={styles.gradientEnd} />
          {/* Glass Reflection Overlay */}
          <View style={styles.glassReflection} />
          {/* Glass Highlight */}
          <View style={styles.glassHighlight} />
          {/* Noise Overlay */}
          <View style={styles.noiseOverlay} />
          <NoiseOverlay />
        </View>

        {/* Close Button - Line 1 and Line 2 */}
        <TouchableOpacity style={styles.closeButton} onPress={onClose}>
          <View style={styles.closeIcon}>
            <View style={[styles.closeLine, styles.closeLine1]} />
            <View style={[styles.closeLine, styles.closeLine2]} />
          </View>
        </TouchableOpacity>

        {/* User Profile Section */}
        <View style={styles.userProfile}>
          <Image
            source={require('./img/aiAtivo 1logo.png')}
            style={styles.userAvatar}
            resizeMode="cover"
          />
          <Text style={styles.userGreeting}>Oi, Carolline</Text>
        </View>

        {/* Menu Items with Gradient Borders */}
        {menuItems.map((item, index) => (
          <View key={item.id} style={[styles.menuItemContainer, { top: item.y - 23 }]}>
            {/* Gradient Border Rectangle */}
            <View style={[styles.menuItemBorder, { height: item.height }]}>
              {/* Top gradient line */}
              <View style={styles.menuItemBorderTop} />
              {/* Bottom gradient line */}
              <View style={styles.menuItemBorderBottom} />
            </View>
            {/* Menu Item Text */}
            <View style={[styles.menuItemTextContainer, { height: item.height }]}>
              <Text style={styles.menuItemText}>{item.title}</Text>
            </View>
          </View>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 1000,
  },
  backdrop: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  menuContainer: {
    position: 'absolute',
    top: 23, // Exact Figma y position
    right: 0,
    width: 229, // Exact Figma width
    height: 570, // Exact Figma height
  },
  backgroundRect: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Base background for gradient
    backgroundColor: 'transparent',
    // Glass border effect
    borderLeftWidth: 1,
    borderLeftColor: 'rgba(255, 255, 255, 0.4)',
    // Rounded corner at bottom-left
    borderBottomLeftRadius: 70, // Exact Figma corner radius
    // Enhanced glass effects
    backdropFilter: 'blur(8px)',
    // Glass reflection effect
    borderTopWidth: 1,
    borderTopColor: 'rgba(255, 255, 255, 0.2)',
    borderRightWidth: 1,
    borderRightColor: 'rgba(255, 255, 255, 0.1)',
    borderBottomWidth: 1,
    borderBottomColor: 'rgba(255, 255, 255, 0.1)',
    overflow: 'hidden',
  },
  gradientBase: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Start color: #217D89 with 90% opacity
    backgroundColor: 'rgba(33, 125, 137, 0.9)',
  },
  gradientTransition: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Middle transition color
    backgroundColor: 'rgba(28, 118, 130, 0.9)', // Mid-point between #217D89 and #1F707B
    opacity: 0.3, // Subtle transition
  },
  gradientEnd: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // End color: #1F707B with 90% opacity
    backgroundColor: 'rgba(31, 112, 123, 0.9)',
    // Create gradient effect by using a mask-like approach
    opacity: 0.4, // This will blend with the base color to create gradient effect
  },
  glassReflection: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Glass reflection effect
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    // Create a subtle gradient reflection
    borderTopWidth: 1,
    borderTopColor: 'rgba(255, 255, 255, 0.3)',
    borderLeftWidth: 1,
    borderLeftColor: 'rgba(255, 255, 255, 0.2)',
  },
  glassHighlight: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: 50, // Top highlight area
    // Glass highlight effect
    backgroundColor: 'rgba(255, 255, 255, 0.15)',
    borderTopLeftRadius: 70,
  },
  noiseOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Glass texture noise
    backgroundColor: 'rgba(0, 0, 0, 0.15)',
    opacity: 0.6,
  },
  closeButton: {
    position: 'absolute',
    top: 20, // 43 - 23 = 20
    right: 20, // Fixed positioning
    width: 20,
    height: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeIcon: {
    width: 18.38, // Exact Figma width
    height: 18.38,
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeLine: {
    position: 'absolute',
    width: 18.38,
    height: 3, // Exact Figma strokeWeight
    backgroundColor: '#fff', // Exact Figma color
  },
  closeLine1: {
    transform: [{ rotate: '45deg' }],
  },
  closeLine2: {
    transform: [{ rotate: '-45deg' }],
  },
  userProfile: {
    position: 'absolute',
    top: 69, // 92 - 23 = 69
    left: 11, // 186 - 175 = 11
    flexDirection: 'row',
    alignItems: 'center',
  },
  userAvatar: {
    width: 55, // Exact Figma width
    height: 55, // Exact Figma height
    borderRadius: 27.5,
  },
  userGreeting: {
    marginLeft: 15, // 256 - 186 - 55 = 15
    fontSize: 20,
    fontWeight: '600',
    color: '#fff', // Exact Figma color
  },
  menuItemContainer: {
    position: 'absolute',
    left: 3, // 178 - 175 = 3
    width: 226, // Exact Figma width
  },
  menuItemBorder: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    // No border width - this will be a gradient stroke
    borderWidth: 0,
    borderRadius: 0,
    // Drop shadow: radius 26.3, opacity 0.25, spread 6
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.25,
    shadowRadius: 26.3,
    elevation: 8,
  },
  menuItemBorderTop: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: 1,
    // Gradient effect: fade from transparent to solid in center
    backgroundColor: 'rgba(47, 212, 205, 0.1)',
    // Create gradient effect with multiple overlays
    shadowColor: '#2FD4CD',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.6,
    shadowRadius: 2,
  },
  menuItemBorderBottom: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    height: 1,
    // Gradient effect: fade from transparent to solid in center
    backgroundColor: 'rgba(47, 212, 205, 0.1)',
    // Create gradient effect with multiple overlays
    shadowColor: '#2FD4CD',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.6,
    shadowRadius: 2,
  },
  menuItemTextContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  menuItemText: {
    fontSize: 20,
    fontWeight: '500',
    color: '#fff', // Exact Figma color
    textAlign: 'center',
  },
  noiseContainer: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'transparent',
  },
  noiseDot: {
    position: 'absolute',
    backgroundColor: 'rgba(0, 0, 0, 0.25)',
    borderRadius: 0.5,
  },
});

export default SideMenu;
