  const renderStats = () => {
    const totalSports = sportsData.length;
    const totalClasses = sportsData.reduce((sum, sport) => sum + sport.totalClasses, 0);
    const totalScore = sportsData.reduce((sum, sport) => sum + sport.totalScore, 0);
    const averageScore = sportsData.length > 0 
      ? Math.round(sportsData.reduce((sum, sport) => sum + sport.averageScore, 0) / sportsData.length)
      : 0;

    return (
      <View style={styles.statsContainer}>
        {/* Card de Pontua√ß√£o Geral */}
        <View style={styles.generalScoreCard}>
          <View style={styles.generalScoreHeader}>
            <Text style={styles.generalScoreTitle}>üèÜ Pontua√ß√£o Geral</Text>
            <View style={[styles.generalScoreBadge, { backgroundColor: getScoreColor(averageScore) }]}>
              <Text style={styles.generalScoreNumber}>{averageScore}</Text>
            </View>
          </View>
          <View style={styles.generalScoreDetails}>
            <View style={styles.generalScoreDetail}>
              <Text style={styles.generalScoreDetailNumber}>{totalScore}</Text>
              <Text style={styles.generalScoreDetailLabel}>Pontos Totais</Text>
            </View>
            <View style={styles.generalScoreDetail}>
              <Text style={styles.generalScoreDetailNumber}>{totalClasses}</Text>
              <Text style={styles.generalScoreDetailLabel}>Aulas</Text>
            </View>
            <View style={styles.generalScoreDetail}>
              <Text style={styles.generalScoreDetailNumber}>{totalSports}</Text>
              <Text style={styles.generalScoreDetailLabel}>Esportes</Text>
            </View>
          </View>
          <Text style={[styles.generalScoreLabel, { color: getScoreColor(averageScore) }]}>
            {getScoreLabel(averageScore)}
          </Text>
        </View>

        {/* Cards de Estat√≠sticas */}
        <View style={styles.statsCards}>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{totalSports}</Text>
            <Text style={styles.statLabel}>Esportes</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{totalClasses}</Text>
            <Text style={styles.statLabel}>Aulas</Text>
          </View>
          <View style={styles.statCard}>
            <Text style={styles.statNumber}>{totalScore}</Text>
            <Text style={styles.statLabel}>Pontos</Text>
          </View>
        </View>
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.safeArea}>
      <SideMenu
        isVisible={isMenuVisible}
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
        currentUser={currentUser}
        onLogout={onLogout}
        userType="STUDENT"
      />
      <ScrollView
        style={styles.container}
        contentContainerStyle={styles.contentContainer}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} colors={['#F9BB55']} />
        }
      >
        {/* Header */}
        <View style={styles.header}>
          <View style={styles.headerLeft}>
            <Text style={styles.title}>Minhas Pontua√ß√µes</Text>
            <Text style={styles.subtitle}>Acompanhe seu desempenho nos esportes</Text>
          </View>
          <HamburgerButton
            onPress={() => setIsMenuVisible(true)}
            style={styles.menuButton}
          />
        </View>

        {isLoading ? (
          <ActivityIndicator size="large" color="#F9BB55" style={styles.loadingIndicator} />
        ) : error ? (
          <View style={styles.errorContainer}>
            <Text style={styles.errorText}>{error}</Text>
            <TouchableOpacity style={styles.retryButton} onPress={loadSportsScores}>
              <Text style={styles.retryButtonText}>Tentar Novamente</Text>
            </TouchableOpacity>
          </View>
        ) : sportsData.length > 0 ? (
          <View style={styles.content}>
            {/* Estat√≠sticas */}
            {renderStats()}

            {/* Lista de Esportes */}
            <View style={styles.sportsList}>
              {sportsData.map(renderSportCard)}
            </View>
          </View>
        ) : (
          <View style={styles.emptyStateContainer}>
            <Text style={styles.emptyStateText}>
              Voc√™ ainda n√£o possui pontua√ß√µes registradas.
            </Text>
            <Text style={styles.emptyStateSubtext}>
              Participe das aulas para come√ßar a acumular pontos!
            </Text>
            <TouchableOpacity style={styles.retryButton} onPress={loadSportsScores}>
              <Text style={styles.retryButtonText}>Atualizar</Text>
            </TouchableOpacity>
          </View>
        )}
      </ScrollView>
    </SafeAreaView>
  );
};

