import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Dimensions,
  Animated,
  Image,
} from 'react-native';

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

// Exact Figma noise component: 0.5 size, monotone, 0.9 density, rgba(0,0,0,0.25)
const NoiseOverlay = () => {
  const noiseDots = Array.from({ length: 1200 }, (_, i) => (
    <View
      key={i}
      style={[
        styles.noiseDot,
        {
          left: Math.random() * 100 + '%',
          top: Math.random() * 100 + '%',
          opacity: Math.random() * 0.3 + 0.1, // 0.9 density - more visible
          width: Math.random() * 0.5 + 0.2, // 0.5 size
          height: Math.random() * 0.5 + 0.2, // 0.5 size
          backgroundColor: 'rgba(0, 0, 0, 0.25)', // Monotone black
        },
      ]}
    />
  ));

  return <View style={styles.noiseContainer}>{noiseDots}</View>;
};

const SideMenu = ({ isVisible, onClose }) => {
  const slideAnim = React.useRef(new Animated.Value(screenWidth)).current;

  React.useEffect(() => {
    if (isVisible) {
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 300,
        useNativeDriver: true,
      }).start();
    } else {
      Animated.timing(slideAnim, {
        toValue: screenWidth,
        duration: 300,
        useNativeDriver: true,
      }).start();
    }
  }, [isVisible, slideAnim]);

  if (!isVisible) return null;

  const menuItems = [
    { id: 'perfil', title: 'Perfil', y: 200, height: 42 },
    { id: 'ranking', title: 'Ranking', y: 269, height: 42 },
    { id: 'chat', title: 'Chat', y: 339, height: 41 },
    { id: 'tutorial', title: 'Tutorial', y: 408, height: 42 },
    { id: 'menu', title: 'Menu', y: 477, height: 42 },
  ];

  return (
    <View style={styles.overlay}>
      <TouchableOpacity style={styles.backdrop} onPress={onClose} />
      <Animated.View
        style={[
          styles.menuContainer,
          {
            transform: [{ translateX: slideAnim }],
          },
        ]}
      >
        {/* Background SVG */}
        <Image 
          source={require('./img/SideMenu.svg')} 
          style={styles.backgroundSvg}
          resizeMode="cover"
        />

        {/* Close Button - Line 1 and Line 2 */}
        <TouchableOpacity style={styles.closeButton} onPress={onClose}>
          <View style={styles.closeIcon}>
            <View style={[styles.closeLine, styles.closeLine1]} />
            <View style={[styles.closeLine, styles.closeLine2]} />
          </View>
        </TouchableOpacity>

        {/* User Profile Section */}
        <View style={styles.userProfile}>
          <Image
            source={require('./img/aiAtivo 1logo.png')}
            style={styles.userAvatar}
            resizeMode="cover"
          />
          <Text style={styles.userGreeting}>Oi, Carolline</Text>
        </View>

            {/* Menu Items with Gradient Borders */}
            {menuItems.map((item, index) => (
              <View key={item.id} style={[styles.menuItemContainer, { top: item.y - 23 }]}>
                {/* Gradient Border Rectangle */}
                <View style={[styles.menuItemBorder, { height: item.height }]}>
                  {/* Top gradient line with multiple layers */}
                  <View style={styles.menuItemBorderTop}>
                    {/* Gradient layer 1: transparent to solid cyan */}
                    <View style={styles.gradientLayer1} />
                    {/* Gradient layer 2: white center */}
                    <View style={styles.gradientLayer2} />
                    {/* Gradient layer 3: solid cyan to transparent */}
                    <View style={styles.gradientLayer3} />
                    {/* Left fade layer for transparent start */}
                    <View style={styles.gradientFadeLeft} />
                    {/* Right fade layer for transparent end */}
                    <View style={styles.gradientFadeRight} />
                  </View>
                  {/* Bottom gradient line with multiple layers */}
                  <View style={styles.menuItemBorderBottom}>
                    {/* Gradient layer 1: transparent to solid cyan */}
                    <View style={styles.gradientLayer1} />
                    {/* Gradient layer 2: white center */}
                    <View style={styles.gradientLayer2} />
                    {/* Gradient layer 3: solid cyan to transparent */}
                    <View style={styles.gradientLayer3} />
                    {/* Left fade layer for transparent start */}
                    <View style={styles.gradientFadeLeft} />
                    {/* Right fade layer for transparent end */}
                    <View style={styles.gradientFadeRight} />
                  </View>
                </View>
                {/* Menu Item Text */}
                <View style={[styles.menuItemTextContainer, { height: item.height }]}>
                  <Text style={styles.menuItemText}>{item.title}</Text>
                </View>
              </View>
            ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 1000,
  },
  backdrop: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  menuContainer: {
    position: 'absolute',
    top: 23, // Exact Figma y position
    right: 0,
    width: 229, // Exact Figma width
    height: 570, // Exact Figma height
  },
  backgroundRect: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Main gradient: r: 0.129*255=33, g: 0.490*255=125, b: 0.537*255=137, opacity: 0.9
    // This simulates the first gradient from the Figma
    backgroundColor: 'rgba(33, 125, 137, 0.9)',
    // Exact Figma border: rgba(255, 255, 255, 0.34)
    borderLeftWidth: 1,
    borderLeftColor: 'rgba(255, 255, 255, 0.34)',
    // Rounded corner at bottom-left
    borderBottomLeftRadius: 70,
    // Background blur: 4px radius (exact Figma)
    backdropFilter: 'blur(4px)',
    overflow: 'hidden',
  },
  gradientOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Second gradient: black to white with 20% opacity
    // Position 0.269: rgba(0, 0, 0, 0.9) with 20% opacity = 0.18
    // Position 0.803: rgba(255, 255, 255, 0.83) with 20% opacity = 0.166
    backgroundColor: 'rgba(0, 0, 0, 0.15)', // Simulated gradient effect
    opacity: 0.2, // 20% opacity as per Figma
    // Rounded corner at bottom-left
    borderBottomLeftRadius: 70,
  },
  noiseOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Exact Figma noise: rgba(0, 0, 0, 0.25) with 0.9 density
    backgroundColor: 'rgba(0, 0, 0, 0.25)',
    opacity: 0.9,
    // Rounded corner at bottom-left
    borderBottomLeftRadius: 70,
  },
  closeButton: {
    position: 'absolute',
    top: 20, // 43 - 23 = 20
    right: 20, // 371 - 175 = 196, but we need it at right edge
    width: 20,
    height: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeIcon: {
    width: 18.38, // Exact Figma width
    height: 18.38,
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeLine: {
    position: 'absolute',
    width: 18.38,
    height: 3, // Exact Figma strokeWeight
    backgroundColor: '#fff', // Exact Figma color
  },
  closeLine1: {
    transform: [{ rotate: '45deg' }],
  },
  closeLine2: {
    transform: [{ rotate: '-45deg' }],
  },
  userProfile: {
    position: 'absolute',
    top: 69, // 92 - 23 = 69
    left: 11, // 186 - 175 = 11
    flexDirection: 'row',
    alignItems: 'center',
  },
  userAvatar: {
    width: 55, // Exact Figma width
    height: 55, // Exact Figma height
    borderRadius: 27.5,
  },
  userGreeting: {
    marginLeft: 15, // 256 - 186 - 55 = 15
    fontSize: 20,
    fontWeight: '600',
    color: '#fff', // Exact Figma color
  },
  menuItemContainer: {
    position: 'absolute',
    left: 3, // 178 - 175 = 3
    width: 226, // Exact Figma width
  },
  menuItemBorder: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    // No border width - this will be a gradient stroke
    borderWidth: 0,
    borderRadius: 0,
    // Drop shadow: radius 26.3, opacity 0.25, spread 6
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.25,
    shadowRadius: 26.3,
    elevation: 8,
  },
  menuItemBorderTop: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: 0.5, // Thinner line
    // Exact Figma gradient stroke with 5 color stops
    // Position 0: rgba(47, 212, 205, 0) - transparent cyan
    // Position 0.096: rgba(47, 212, 205, 1) - solid cyan
    // Position 0.5: rgba(255, 255, 255, 1) - white
    // Position 0.899: rgba(47, 212, 205, 1) - solid cyan
    // Position 1: rgba(47, 212, 205, 0) - transparent cyan
    backgroundColor: 'transparent', // Transparent base
    // Create gradient effect with multiple overlays
    shadowColor: '#2FD4CD',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.6,
    shadowRadius: 2,
    elevation: 1,
  },
  menuItemBorderBottom: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    height: 0.5, // Thinner line
    // Exact Figma gradient stroke with 5 color stops
    // Position 0: rgba(47, 212, 205, 0) - transparent cyan
    // Position 0.096: rgba(47, 212, 205, 1) - solid cyan
    // Position 0.5: rgba(255, 255, 255, 1) - white
    // Position 0.899: rgba(47, 212, 205, 1) - solid cyan
    // Position 1: rgba(47, 212, 205, 0) - transparent cyan
    backgroundColor: 'transparent', // Transparent base
    // Create gradient effect with multiple overlays
    shadowColor: '#2FD4CD',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.6,
    shadowRadius: 2,
    elevation: 1,
  },
  menuItemTextContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  menuItemText: {
    fontSize: 20,
    fontWeight: '500',
    color: '#fff', // Exact Figma color
    textAlign: 'center',
  },
  gradientLayer1: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: 0.5, // Thinner line
    // Position 0-0.096: transparent to solid cyan
    backgroundColor: 'rgba(47, 212, 205, 0.4)',
    opacity: 0.6,
  },
  gradientLayer2: {
    position: 'absolute',
    top: 0,
    left: '45%', // Position 0.5 (center) - slightly wider for better blend
    right: '45%',
    height: 0.5, // Thinner line
    // Position 0.5: white center
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    opacity: 0.7,
  },
  gradientLayer3: {
    position: 'absolute',
    top: 0,
    right: 0,
    width: '15%', // Position 0.899-1: solid cyan to transparent - shorter for better fade
    height: 0.5, // Thinner line
    backgroundColor: 'rgba(47, 212, 205, 0.4)',
    opacity: 0.6,
  },
  gradientFadeLeft: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '10%', // Left fade area
    height: 0.5,
    backgroundColor: 'rgba(47, 212, 205, 0.1)', // Very transparent
    opacity: 0.3,
  },
  gradientFadeRight: {
    position: 'absolute',
    top: 0,
    right: 0,
    width: '10%', // Right fade area
    height: 0.5,
    backgroundColor: 'rgba(47, 212, 205, 0.1)', // Very transparent
    opacity: 0.3,
  },
  noiseContainer: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'transparent',
  },
  noiseDot: {
    position: 'absolute',
    backgroundColor: 'rgba(0, 0, 0, 0.25)',
    borderRadius: 0.5,
  },
});

export default SideMenu;
