import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  SafeAreaView,
  Alert,
  TextInput,
} from 'react-native';
import apiService from '../../services/apiService';
import SideMenu from '../../components/SideMenu';
import CustomModal from '../../components/CustomModal';

const { width, height } = Dimensions.get('window');

const TeacherClassesScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  const [classes, setClasses] = useState([]);
  const [filteredClasses, setFilteredClasses] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [searchText, setSearchText] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [modalVisible, setModalVisible] = useState(false);
  const [modalConfig, setModalConfig] = useState({});

  // Carregar aulas do professor
  useEffect(() => {
    loadClasses();
  }, []);

  // Recarregar aulas quando voltar da tela de aula
  useEffect(() => {
    const handleFocus = () => {
      loadClasses();
    };

    // Simular evento de foco (em uma aplicaﾃｧﾃ｣o real usaria useFocusEffect do React Navigation)
    const interval = setInterval(() => {
      // Verificar se hﾃ｡ mudanﾃｧas nos parﾃ｢metros de navegaﾃｧﾃ｣o
      if (window.navigationParams && window.navigationParams.classCompleted) {
        loadClasses();
        // Limpar o parﾃ｢metro para evitar recarregamentos desnecessﾃ｡rios
        window.navigationParams.classCompleted = false;
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Filtrar aulas quando searchText ou selectedFilter mudar
  useEffect(() => {
    filterClasses();
  }, [classes, searchText, selectedFilter]);

  const loadClasses = async () => {
    try {
      setIsLoading(true);
      const response = await apiService.getTeacherClasses();
      
      if (response.success) {
        // CORREﾃﾃグ: API agora retorna array diretamente
        console.log('鳩 loadClasses - Dados recebidos do backend:', response.data);
        
        // Verificar se ﾃｩ array ou objeto (compatibilidade)
        let classesArray;
        if (Array.isArray(response.data)) {
          // API retorna array diretamente
          classesArray = response.data.map(classData => {
            console.log('鳩 loadClasses - Processando aula:', {
              id: classData.id,
              classData: classData,
              dateType: typeof classData.date
            });
            return {
              id: classData.id,
              classId: classData.classId, // ID da turma associada
              date: classData.date, // CORREﾃﾃグ: Manter como string ao invﾃｩs de converter para Date
              school: classData.school,
              grade: classData.grade,
              subject: classData.subject,
              time: classData.time,
              notes: classData.notes,
              isCompleted: classData.isCompleted
            };
          });
        } else {
          // Fallback: converter objeto para array (compatibilidade)
          classesArray = Object.entries(response.data).map(([date, classData]) => {
            console.log('鳩 loadClasses - Processando aula (fallback):', {
              dateKey: date,
              classData: classData,
              dateType: typeof date
            });
            return {
              id: classData.id,
              classId: classData.classId, // ID da turma associada
              date: date, // CORREﾃﾃグ: Manter como string ao invﾃｩs de converter para Date
              school: classData.school,
              grade: classData.grade,
              subject: classData.subject,
              time: classData.time,
              notes: classData.notes,
              isCompleted: classData.isCompleted
            };
          });
        }
        
        // Ordenar por data (mais recente primeiro) - CORREﾃﾃグ: trabalhar com strings
        classesArray.sort((a, b) => {
          // Converter strings para Date apenas para comparaﾃｧﾃ｣o
          const dateA = new Date(a.date);
          const dateB = new Date(b.date);
          return dateB - dateA;
        });
        
        setClasses(classesArray);
      }
    } catch (error) {
      console.error('Erro ao carregar aulas:', error);
      Alert.alert('Erro', 'Nﾃ｣o foi possﾃｭvel carregar as aulas');
    } finally {
      setIsLoading(false);
    }
  };

  const filterClasses = () => {
    let filtered = classes;

    // Filtrar por texto de busca
    if (searchText.trim()) {
      filtered = filtered.filter(cls => 
        cls.school.toLowerCase().includes(searchText.toLowerCase()) ||
        cls.grade.toLowerCase().includes(searchText.toLowerCase()) ||
        (cls.subject && cls.subject.toLowerCase().includes(searchText.toLowerCase()))
      );
    }

    // Filtrar por tipo
    switch (selectedFilter) {
      case 'completed':
        filtered = filtered.filter(cls => cls.isCompleted);
        break;
      case 'pending':
        filtered = filtered.filter(cls => !cls.isCompleted);
        break;
      case 'today':
        const today = new Date();
        filtered = filtered.filter(cls => 
          cls.date.toDateString() === today.toDateString()
        );
        break;
      case 'thisWeek':
        const startOfWeek = new Date();
        startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay());
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(endOfWeek.getDate() + 6);
        filtered = filtered.filter(cls => 
          cls.date >= startOfWeek && cls.date <= endOfWeek
        );
        break;
      default:
        // 'all' - nﾃ｣o filtrar
        break;
    }

    setFilteredClasses(filtered);
  };

  const formatDate = (date) => {
    // CORREﾃﾃグ: date agora ﾃｩ uma string no formato YYYY-MM-DD
    console.log('鳩 formatDate - Input:', date, 'Type:', typeof date);
    
    if (typeof date === 'string') {
      // Converter string YYYY-MM-DD para formato brasileiro DD/MM/YYYY
      const [year, month, day] = date.split('-');
      const result = `${day}/${month}/${year}`;
      console.log('鳩 formatDate - String conversion:', {
        input: date,
        year, month, day,
        result
      });
      return result;
    } else if (date instanceof Date) {
      // Fallback para objetos Date (caso ainda existam)
      const result = date.toLocaleDateString('pt-BR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      });
      console.log('鳩 formatDate - Date conversion:', {
        input: date,
        result
      });
      return result;
    } else {
      console.log('鳩 formatDate - Invalid input:', date);
      return 'Data invﾃ｡lida';
    }
  };

  const formatTime = (time) => {
    if (!time) return '';
    return time;
  };

  const getStatusColor = (isCompleted) => {
    return isCompleted ? '#4CAF50' : '#FF9800';
  };

  const getStatusText = (isCompleted) => {
    return isCompleted ? 'Concluﾃｭda' : 'Pendente';
  };

  const handleCreateClass = () => {
    onNavigate('createClass');
  };

  const handleEditClass = (classId) => {
    onNavigate('editClass', { classId });
  };

  const handleStartClass = (classItem) => {
    onNavigate('class', { classData: classItem });
  };

  const showModal = (config) => {
    setModalConfig(config);
    setModalVisible(true);
  };

  const hideModal = () => {
    setModalVisible(false);
    setModalConfig({});
  };

  const handleDeleteClass = async (classId) => {
    console.log('handleDeleteClass chamado com ID:', classId);
    
    showModal({
      type: 'warning',
      title: 'Confirmar Exclusﾃ｣o',
      message: 'Tem certeza que deseja excluir esta aula? Esta aﾃｧﾃ｣o nﾃ｣o pode ser desfeita.',
      buttons: [
        {
          text: 'Cancelar',
          style: 'secondary',
          onPress: () => {
            console.log('Exclusﾃ｣o cancelada pelo usuﾃ｡rio');
            hideModal();
          }
        },
        {
          text: 'Excluir',
          style: 'danger',
          onPress: async () => {
            console.log('Confirmando exclusﾃ｣o da aula:', classId);
            try {
              const response = await apiService.deleteClass(classId);
              console.log('Resposta da API:', response);
              if (response.success) {
                await loadClasses();
                hideModal();
                showModal({
                  type: 'success',
                  title: 'Sucesso',
                  message: 'Aula excluﾃｭda com sucesso!',
                  buttons: [
                    {
                      text: 'OK',
                      style: 'primary',
                      onPress: hideModal
                    }
                  ]
                });
              } else {
                hideModal();
                showModal({
                  type: 'error',
                  title: 'Erro',
                  message: response.message || 'Nﾃ｣o foi possﾃｭvel excluir a aula',
                  buttons: [
                    {
                      text: 'OK',
                      style: 'primary',
                      onPress: hideModal
                    }
                  ]
                });
              }
            } catch (error) {
              console.error('Erro ao excluir aula:', error);
              hideModal();
              showModal({
                type: 'error',
                title: 'Erro',
                message: 'Nﾃ｣o foi possﾃｭvel excluir a aula',
                buttons: [
                  {
                    text: 'OK',
                    style: 'primary',
                    onPress: hideModal
                  }
                ]
              });
            }
          }
        }
      ]
    });
  };

  const toggleClassStatus = async (classId, isCompleted) => {
    try {
      const response = await apiService.completeClass(classId, !isCompleted);
      if (response.success) {
        await loadClasses();
      }
    } catch (error) {
      Alert.alert('Erro', 'Nﾃ｣o foi possﾃｭvel atualizar o status da aula');
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)} 
        onNavigate={onNavigate}
        currentUser={currentUser}
        onLogout={onLogout}
        userType="TEACHER"
      />
      
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.logo}>Movz</Text>
          <TouchableOpacity 
            style={styles.menuButton}
            onPress={() => setIsMenuVisible(!isMenuVisible)}
          >
            <View style={styles.menuIcon}>
              <View style={styles.menuLine} />
              <View style={styles.menuLine} />
              <View style={styles.menuLine} />
            </View>
          </TouchableOpacity>
        </View>

        {/* Title */}
        <Text style={styles.title}>Minhas Aulas</Text>
        <Text style={styles.subtitle}>
          Gerencie suas aulas e acompanhe seu progresso
        </Text>

        {/* Search and Filters */}
        <View style={styles.searchContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Buscar por escola, sﾃｩrie ou matﾃｩria..."
            value={searchText}
            onChangeText={setSearchText}
          />
        </View>

        <View style={styles.filtersContainer}>
          <ScrollView horizontal showsHorizontalScrollIndicator={false}>
            {[
              { key: 'all', label: 'Todas' },
              { key: 'today', label: 'Hoje' },
              { key: 'thisWeek', label: 'Esta Semana' },
              { key: 'pending', label: 'Pendentes' },
              { key: 'completed', label: 'Concluﾃｭdas' }
            ].map((filter) => (
              <TouchableOpacity
                key={filter.key}
                style={[
                  styles.filterButton,
                  selectedFilter === filter.key && styles.filterButtonActive
                ]}
                onPress={() => setSelectedFilter(filter.key)}
              >
                <Text style={[
                  styles.filterButtonText,
                  selectedFilter === filter.key && styles.filterButtonTextActive
                ]}>
                  {filter.label}
                </Text>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>

        {/* Create Class Button */}
        <TouchableOpacity style={styles.createButton} onPress={handleCreateClass}>
          <Text style={styles.createButtonText}>+ Nova Aula</Text>
        </TouchableOpacity>

        {/* Classes List */}
        {isLoading ? (
          <View style={styles.loadingContainer}>
            <Text style={styles.loadingText}>Carregando aulas...</Text>
          </View>
        ) : filteredClasses.length === 0 ? (
          <View style={styles.emptyContainer}>
            <Text style={styles.emptyTitle}>Nenhuma aula encontrada</Text>
            <Text style={styles.emptySubtitle}>
              {searchText || selectedFilter !== 'all' 
                ? 'Tente ajustar os filtros ou busca'
                : 'Crie sua primeira aula clicando no botﾃ｣o acima'
              }
            </Text>
          </View>
        ) : (
          <View style={styles.classesList}>
            {filteredClasses.map((classItem) => (
              <View key={classItem.id} style={styles.classCard}>
                {/* Card Header */}
                <View style={styles.cardHeader}>
                  <View style={styles.classInfo}>
                    <Text style={styles.schoolName}>{classItem.school}</Text>
                    <Text style={styles.gradeText}>{classItem.grade}</Text>
                  </View>
                  <View style={styles.statusContainer}>
                    <View style={[
                      styles.statusBadge,
                      { backgroundColor: getStatusColor(classItem.isCompleted) }
                    ]}>
                      <Text style={styles.statusText}>
                        {getStatusText(classItem.isCompleted)}
                      </Text>
                    </View>
                  </View>
                </View>

                {/* Card Body */}
                <View style={styles.cardBody}>
                  <View style={styles.dateTimeContainer}>
                    <Text style={styles.dateText}>
                      套 {formatDate(classItem.date)}
                    </Text>
                    {classItem.time && (
                      <Text style={styles.timeText}>
                        武 {formatTime(classItem.time)}
                      </Text>
                    )}
                  </View>
                  
                  {classItem.subject && (
                    <Text style={styles.subjectText}>
                      答 {classItem.subject}
                    </Text>
                  )}

                  {classItem.notes && (
                    <Text style={styles.notesText} numberOfLines={2}>
                      統 {classItem.notes}
                    </Text>
                  )}
                </View>

                {/* Card Actions */}
                <View style={styles.cardActions}>
                  {!classItem.isCompleted && (
                    <TouchableOpacity
                      style={[styles.actionButton, styles.startButton]}
                      onPress={() => handleStartClass(classItem)}
                    >
                      <Text style={styles.actionButtonText}>Iniciar Aula</Text>
                    </TouchableOpacity>
                  )}
                  
                  <TouchableOpacity
                    style={styles.actionButton}
                    onPress={() => handleEditClass(classItem.id)}
                  >
                    <Text style={styles.actionButtonText}>Editar</Text>
                  </TouchableOpacity>
                  
                  <TouchableOpacity
                    style={[
                      styles.actionButton,
                      classItem.isCompleted ? styles.completeButton : styles.pendingButton
                    ]}
                    onPress={() => toggleClassStatus(classItem.id, classItem.isCompleted)}
                  >
                    <Text style={styles.actionButtonText}>
                      {classItem.isCompleted ? 'Marcar Pendente' : 'Marcar Concluﾃｭda'}
                    </Text>
                  </TouchableOpacity>
                  
                  <TouchableOpacity
                    style={[styles.actionButton, styles.deleteButton]}
                    onPress={() => handleDeleteClass(classItem.id)}
                  >
                    <Text style={styles.actionButtonText}>Excluir</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}
          </View>
        )}

        {/* Stats */}
        {classes.length > 0 && (
          <View style={styles.statsContainer}>
            <Text style={styles.statsTitle}>Resumo</Text>
            <View style={styles.statsRow}>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>{classes.length}</Text>
                <Text style={styles.statLabel}>Total de Aulas</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>
                  {classes.filter(c => c.isCompleted).length}
                </Text>
                <Text style={styles.statLabel}>Concluﾃｭdas</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>
                  {classes.filter(c => !c.isCompleted).length}
                </Text>
                <Text style={styles.statLabel}>Pendentes</Text>
              </View>
            </View>
          </View>
        )}

        {/* Modal Personalizado */}
        <CustomModal
          visible={modalVisible}
          onClose={hideModal}
          title={modalConfig.title || ''}
          message={modalConfig.message || ''}
          type={modalConfig.type || 'info'}
          buttons={modalConfig.buttons || []}
        />
      </ScrollView>
    </SafeAreaView>
  );
};

