import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Dimensions,
  Animated,
  Image,
} from 'react-native';

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');


const SideMenu = ({ isVisible, onClose, onNavigate, currentUser, onLogout, userType = 'STUDENT' }) => {
  const slideAnim = React.useRef(new Animated.Value(screenWidth)).current;

  React.useEffect(() => {
    if (isVisible) {
      // Disable scroll when menu is open
      const originalStyle = window.getComputedStyle(document.body).overflow;
      document.body.style.overflow = 'hidden';
      document.body.style.position = 'fixed';
      document.body.style.width = '100%';
      
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 300,
        useNativeDriver: false,
      }).start();
    } else {
      // Re-enable scroll when menu is closed
      document.body.style.overflow = 'unset';
      document.body.style.position = 'unset';
      document.body.style.width = 'unset';
      
      Animated.timing(slideAnim, {
        toValue: screenWidth,
        duration: 300,
        useNativeDriver: false,
      }).start();
    }

    // Cleanup function to ensure scroll is re-enabled
    return () => {
      document.body.style.overflow = 'unset';
      document.body.style.position = 'unset';
      document.body.style.width = 'unset';
    };
  }, [isVisible, slideAnim]);

  if (!isVisible) return null;

  const getMenuItems = () => {
    if (userType === 'TEACHER') {
      return [
        { id: 'agenda', title: 'Agenda de Aulas', y: 200, height: 42, screen: 'teacherSchedule' },
        { id: 'classes', title: 'Minhas Aulas', y: 269, height: 42, screen: 'teacherClasses' },
        { id: 'myClasses', title: 'Minhas Turmas', y: 339, height: 42, screen: 'myClasses' },
        { id: 'perfil', title: 'Meus Dados', y: 408, height: 42, screen: 'teacherProfile' },
        { id: 'logout', title: 'Sair', y: 477, height: 42, screen: 'logout' },
      ];
    } else if (userType === 'INSTITUTION') {
      return [
        { id: 'logout', title: 'Sair', y: 200, height: 42, screen: 'logout' },
      ];
    } else {
      // STUDENT
      return [
        { id: 'perfil', title: 'Perfil', y: 200, height: 42, screen: 'home' },
        { id: 'myClass', title: 'Minha Turma', y: 269, height: 42, screen: 'myClass' },
        { id: 'ranking', title: 'Ranking', y: 339, height: 42, screen: 'ranking' },
        { id: 'chat', title: 'Chat', y: 408, height: 41, screen: 'chat' },
        { id: 'tutorial', title: 'Tutorial', y: 477, height: 42, screen: 'tutorial' },
        { id: 'logout', title: 'Sair', y: 546, height: 42, screen: 'logout' },
      ];
    }
  };

  const menuItems = getMenuItems();

  return (
    <View style={styles.overlay}>
      <TouchableOpacity style={styles.backdrop} onPress={onClose} />
      <Animated.View
        style={[
          styles.menuContainer,
          {
            transform: [{ translateX: slideAnim }],
          },
        ]}
      >
        {/* Bottom Circle - Behind rectangle */}
        <View style={styles.bottomCircle} />
        
        {/* Main Background */}
        <View style={styles.background} />
        
        {/* Close Button X in bottom circle */}
        <TouchableOpacity style={styles.bottomCloseButton} onPress={onClose}>
          <Text style={styles.closeX}>×</Text>
        </TouchableOpacity>

        {/* Top Close Button */}
        <TouchableOpacity style={styles.topCloseButton} onPress={onClose}>
          <Text style={styles.topCloseText}>×</Text>
        </TouchableOpacity>

        {/* User Avatar */}
        <View style={styles.avatarContainer}>
          <Image
            source={require('../assets/images/aiAtivo 1logo.png')}
            style={styles.userAvatar}
            resizeMode="cover"
          />
        </View>

        {/* Menu Items */}
        {menuItems.map((item, index) => (
          <TouchableOpacity 
            key={item.id} 
            style={[styles.menuItem, { top: item.y - 23 }]}
            onPress={() => {
              if (item.screen === 'logout') {
                onLogout && onLogout();
              } else {
                onNavigate && onNavigate(item.screen);
              }
            }}
          >
            <Text style={styles.menuItemText}>{item.title}</Text>
          </TouchableOpacity>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 1000,
  },
  backdrop: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.3)', // Simple backdrop blur
  },
  menuContainer: {
    position: 'absolute',
    top: 0,
    right: 0,
    width: 195,
    height: screenHeight,
  },
  // Simple background
  background: {
    position: 'absolute',
    top: 0,
    left: 27,
    width: 168,
    height: '100%',
    backgroundColor: '#F19341', // Solid orange color
    shadowColor: '#000',
    shadowOffset: { width: -2, height: 0 },
    shadowOpacity: 0.2,
    shadowRadius: 4,
    elevation: 8,
  },
  // Bottom circle - partially behind rectangle
  bottomCircle: {
    position: 'absolute',
    bottom: 30,
    left: 10, // Slightly offset so it's partially visible
    width: 56,
    height: 56,
    borderRadius: 28,
    backgroundColor: '#F19341',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 3,
    elevation: 7, // Lower than rectangle
  },
  // Close button in bottom circle
  bottomCloseButton: {
    position: 'absolute',
    bottom: 44, // Perfect center: 30 + 28 - 14 = 44
    left: 24, // Adjusted for the offset circle: 10 + 28 - 14 = 24
    width: 28,
    height: 28,
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeX: {
    fontSize: 20,
    color: '#fff',
    fontWeight: 'bold',
  },
  // Top close button
  topCloseButton: {
    position: 'absolute',
    top: 15,
    right: 15,
    width: 30,
    height: 30,
    justifyContent: 'center',
    alignItems: 'center',
  },
  topCloseText: {
    fontSize: 20,
    color: '#fff',
    fontWeight: 'bold',
  },
  // Avatar container
  avatarContainer: {
    position: 'absolute',
    top: 50,
    left: 80,
    width: 60,
    height: 60,
  },
  userAvatar: {
    width: 60,
    height: 60,
    borderRadius: 30,
  },
  // Menu items
  menuItem: {
    position: 'absolute',
    left: 27,
    width: 168,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
  },
  menuItemText: {
    fontSize: 18,
    fontWeight: '500',
    color: '#fff',
    textAlign: 'center',
  },
});

export default SideMenu;
