const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const morgan = require('morgan');
require('dotenv').config();

const authRoutes = require('./routes/auth');
const userRoutes = require('./routes/users');
const sportRoutes = require('./routes/sports');
const contentRoutes = require('./routes/contents');
const scoreRoutes = require('./routes/scores');
const chatRoutes = require('./routes/chat');
const classRoutes = require('./routes/classes');

const app = express();
const PORT = process.env.PORT || 3001;

// Middleware
app.use(helmet());
app.use(cors({
  origin: process.env.NODE_ENV === 'production' 
    ? ['https://yourdomain.com'] 
    : ['http://localhost:19006', 'http://localhost:19007'],
  credentials: true
}));
app.use(morgan('combined'));
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: true }));

// Health check
app.get('/health', (req, res) => {
  res.json({ 
    status: 'OK', 
    timestamp: new Date().toISOString(),
    environment: process.env.NODE_ENV || 'development'
  });
});

// Routes
app.use('/api/auth', authRoutes);
app.use('/api/users', userRoutes);
app.use('/api/sports', sportRoutes);
app.use('/api/contents', contentRoutes);
app.use('/api/scores', scoreRoutes);
app.use('/api/chat', chatRoutes);
app.use('/api/classes', classRoutes);

// Error handling middleware
app.use((err, req, res, next) => {
  console.error('Error:', err);
  
  if (err.name === 'ValidationError') {
    return res.status(400).json({
      success: false,
      message: 'Dados inv치lidos',
      errors: err.details
    });
  }

  if (err.name === 'UnauthorizedError') {
    return res.status(401).json({
      success: false,
      message: 'Token inv치lido ou expirado'
    });
  }

  res.status(500).json({
    success: false,
    message: 'Erro interno do servidor',
    ...(process.env.NODE_ENV === 'development' && { error: err.message })
  });
});

// 404 handler
app.use((req, res) => {
  res.status(404).json({
    success: false,
    message: 'Endpoint n칚o encontrado'
  });
});

app.listen(PORT, () => {
  console.log(`游 Servidor rodando na porta ${PORT}`);
  console.log(`游늵 Health check: http://localhost:${PORT}/health`);
  console.log(`游깴 Ambiente: ${process.env.NODE_ENV || 'development'}`);
});

module.exports = app;
