import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  SafeAreaView,
  Image,
  ActivityIndicator,
  RefreshControl,
} from 'react-native';
import SideMenu from '../../components/SideMenu';
import apiService from '../../services/apiService';

const { width, height } = Dimensions.get('window');

const AchievementsScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  const [profileData, setProfileData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadAchievementsData();
  }, []);

  const loadAchievementsData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Configurar token de autenticação
      const token = localStorage.getItem('authToken');
      if (token) {
        apiService.setToken(token);
      } else {
        console.error('Token não encontrado');
        setError('Token de autenticação não encontrado');
        return;
      }
      
      const response = await apiService.getStudentProfile();
      if (response.success) {
        setProfileData(response.data);
      } else {
        setError(response.message || 'Erro ao carregar conquistas');
      }
    } catch (err) {
      console.error('Erro ao carregar conquistas:', err);
      setError('Não foi possível carregar as conquistas. Tente novamente.');
    } finally {
      setIsLoading(false);
      setRefreshing(false);
    }
  };

  const onRefresh = () => {
    setRefreshing(true);
    loadAchievementsData();
  };

  const isAchievementUnlocked = (achievement) => {
    if (!profileData) return false;
    return profileData.achievements.unlocked.some(unlockedAchievement => unlockedAchievement.id === achievement.id);
  };

  const renderAchievementItem = (achievement) => {
    const isUnlocked = isAchievementUnlocked(achievement);
    
    return (
      <View key={achievement.id} style={[
        styles.achievementItem,
        isUnlocked && styles.achievementItemUnlocked
      ]}>
        <View style={styles.achievementIconContainer}>
          {isUnlocked ? (
            <Image 
              source={{ uri: achievement.icon }} 
              style={styles.achievementIcon}
              resizeMode="contain"
            />
          ) : (
            <View style={styles.lockedIconContainer}>
              <Text style={styles.lockedIcon}>🔒</Text>
            </View>
          )}
        </View>
        
        <View style={styles.achievementInfo}>
          <Text style={[
            styles.achievementName,
            !isUnlocked && styles.achievementNameLocked
          ]}>
            {achievement.name}
          </Text>
          
          <Text style={[
            styles.achievementDescription,
            !isUnlocked && styles.achievementDescriptionLocked
          ]}>
            {achievement.description}
          </Text>
          
          <View style={styles.achievementDetails}>
            <Text style={[
              styles.achievementCategory,
              !isUnlocked && styles.achievementCategoryLocked
            ]}>
              {achievement.category}
            </Text>
            
            <Text style={[
              styles.achievementRequirement,
              !isUnlocked && styles.achievementRequirementLocked
            ]}>
              {isUnlocked ? '✅ Desbloqueada' : `🔒 ${achievement.requirement}`}
            </Text>
          </View>
        </View>
      </View>
    );
  };

  if (isLoading) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#F9BB55" />
          <Text style={styles.loadingText}>Carregando conquistas...</Text>
        </View>
      </SafeAreaView>
    );
  }

  if (error) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
          <TouchableOpacity style={styles.retryButton} onPress={loadAchievementsData}>
            <Text style={styles.retryButtonText}>Tentar Novamente</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} colors={['#F9BB55']} />
        }
      >
        <View style={styles.header}>
          <Text style={styles.title}>🏆 Conquistas</Text>
          {profileData && (
            <Text style={styles.subtitle}>
              {profileData.achievements.stats.unlocked} de {profileData.achievements.stats.total} conquistas obtidas
            </Text>
          )}
        </View>

        <View style={styles.achievementsList}>
          {profileData?.achievements?.available?.map(achievement => renderAchievementItem(achievement))}
        </View>
      </ScrollView>
      
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
        currentUser={currentUser}
        onLogout={onLogout}
        userType="STUDENT"
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EDED',
  },
  scrollView: {
    flex: 1,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
    fontFamily: 'Poppins',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  errorText: {
    fontSize: 16,
    color: '#D9493C',
    textAlign: 'center',
    marginBottom: 20,
    fontFamily: 'Poppins',
  },
  retryButton: {
    backgroundColor: '#F9BB55',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 8,
  },
  retryButtonText: {
    color: '#000',
    fontWeight: 'bold',
    fontFamily: 'Poppins',
  },
  header: {
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 10,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    marginTop: 5,
    fontFamily: 'Poppins',
  },
  achievementsList: {
    paddingHorizontal: 20,
  },
  achievementItem: {
    flexDirection: 'row',
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 15,
    marginBottom: 15,
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    elevation: 3,
    alignItems: 'center',
  },
  achievementItemUnlocked: {
    borderWidth: 2,
    borderColor: '#F9BB55',
  },
  achievementIconContainer: {
    width: 60,
    height: 60,
    borderRadius: 30,
    backgroundColor: '#F5F5F5',
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 15,
  },
  achievementIcon: {
    width: 50,
    height: 50,
  },
  lockedIconContainer: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#E5E7EB',
    justifyContent: 'center',
    alignItems: 'center',
  },
  lockedIcon: {
    fontSize: 24,
    color: '#9CA3AF',
  },
  achievementInfo: {
    flex: 1,
  },
  achievementName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 5,
    fontFamily: 'Poppins',
  },
  achievementNameLocked: {
    color: '#9CA3AF',
  },
  achievementDescription: {
    fontSize: 14,
    color: '#666',
    marginBottom: 8,
    fontFamily: 'Poppins',
  },
  achievementDescriptionLocked: {
    color: '#9CA3AF',
  },
  achievementDetails: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  achievementCategory: {
    fontSize: 12,
    color: '#666',
    fontFamily: 'Poppins',
  },
  achievementCategoryLocked: {
    color: '#9CA3AF',
  },
  achievementRequirement: {
    fontSize: 12,
    color: '#059669',
    fontWeight: '600',
    fontFamily: 'Poppins',
  },
  achievementRequirementLocked: {
    color: '#9CA3AF',
  },
});

export default AchievementsScreen;
