import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  SafeAreaView,
  Image,
  ActivityIndicator,
  RefreshControl,
} from 'react-native';
import SideMenu from '../../components/SideMenu';
import apiService from '../../services/apiService';

const { width, height } = Dimensions.get('window');

const MedalsScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  const [profileData, setProfileData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadMedalsData();
  }, []);

  const loadMedalsData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Configurar token de autenticação
      const token = localStorage.getItem('authToken');
      if (token) {
        apiService.setToken(token);
      } else {
        console.error('Token não encontrado');
        setError('Token de autenticação não encontrado');
        return;
      }
      
      const response = await apiService.getStudentProfile();
      if (response.success) {
        setProfileData(response.data);
      } else {
        setError(response.message || 'Erro ao carregar medalhas');
      }
    } catch (err) {
      console.error('Erro ao carregar medalhas:', err);
      setError('Não foi possível carregar as medalhas. Tente novamente.');
    } finally {
      setIsLoading(false);
      setRefreshing(false);
    }
  };

  const onRefresh = () => {
    setRefreshing(true);
    loadMedalsData();
  };

  const isMedalUnlocked = (medal) => {
    if (!profileData) return false;
    return profileData.medals.unlocked.some(unlockedMedal => unlockedMedal.id === medal.id);
  };

  const renderMedalCard = (medal) => {
    const isUnlocked = isMedalUnlocked(medal);
    
    return (
      <View key={medal.id} style={[
        styles.medalCard,
        isUnlocked && styles.medalCardUnlocked
      ]}>
        <View style={styles.medalHeader}>
          <View style={styles.medalIconContainer}>
            {isUnlocked ? (
              <Image 
                source={{ uri: medal.icon }} 
                style={styles.medalIcon}
                resizeMode="contain"
              />
            ) : (
              <View style={styles.lockedIconContainer}>
                <Text style={styles.lockedIcon}>🔒</Text>
              </View>
            )}
          </View>
          
          {isUnlocked && (
            <View style={styles.checkmarkContainer}>
              <Text style={styles.checkmark}>✅</Text>
            </View>
          )}
        </View>

        <Text style={[
          styles.medalName,
          !isUnlocked && styles.medalNameLocked
        ]}>
          {medal.name}
        </Text>

        <Text style={[
          styles.medalDescription,
          !isUnlocked && styles.medalDescriptionLocked
        ]}>
          {medal.description}
        </Text>

        <View style={styles.medalFooter}>
          <Text style={[
            styles.medalCategory,
            !isUnlocked && styles.medalCategoryLocked
          ]}>
            {medal.category}
          </Text>
          
          <Text style={[
            styles.medalRarity,
            !isUnlocked && styles.medalRarityLocked
          ]}>
            {medal.rarity}
          </Text>
        </View>

        <View style={styles.medalStatus}>
          <Text style={[
            styles.medalStatusText,
            !isUnlocked && styles.medalStatusTextLocked
          ]}>
            {isUnlocked ? 'Desbloqueada' : `${medal.requirement}`}
          </Text>
        </View>
      </View>
    );
  };

  if (isLoading) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#F9BB55" />
          <Text style={styles.loadingText}>Carregando medalhas...</Text>
        </View>
      </SafeAreaView>
    );
  }

  if (error) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
          <TouchableOpacity style={styles.retryButton} onPress={loadMedalsData}>
            <Text style={styles.retryButtonText}>Tentar Novamente</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} colors={['#F9BB55']} />
        }
      >
        <View style={styles.header}>
          <Text style={styles.title}>🏆 Medalhas</Text>
          {profileData && (
            <Text style={styles.subtitle}>
              {profileData.medals.stats.unlocked} de {profileData.medals.stats.total} medalhas obtidas
            </Text>
          )}
        </View>

        <View style={styles.medalsGrid}>
          {profileData?.medals?.available?.map(medal => renderMedalCard(medal))}
        </View>
      </ScrollView>
      
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
        currentUser={currentUser}
        onLogout={onLogout}
        userType="STUDENT"
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EDED',
  },
  scrollView: {
    flex: 1,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
    fontFamily: 'Poppins',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  errorText: {
    fontSize: 16,
    color: '#D9493C',
    textAlign: 'center',
    marginBottom: 20,
    fontFamily: 'Poppins',
  },
  retryButton: {
    backgroundColor: '#F9BB55',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 8,
  },
  retryButtonText: {
    color: '#000',
    fontWeight: 'bold',
    fontFamily: 'Poppins',
  },
  header: {
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 10,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    marginTop: 5,
    fontFamily: 'Poppins',
  },
  medalsGrid: {
    paddingHorizontal: 20,
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  medalCard: {
    width: (width - 60) / 2,
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 15,
    marginBottom: 15,
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    elevation: 3,
  },
  medalCardUnlocked: {
    borderWidth: 2,
    borderColor: '#F9BB55',
  },
  medalHeader: {
    position: 'relative',
    alignItems: 'center',
    marginBottom: 10,
  },
  medalIconContainer: {
    width: 60,
    height: 60,
    borderRadius: 30,
    backgroundColor: '#F5F5F5',
    justifyContent: 'center',
    alignItems: 'center',
  },
  medalIcon: {
    width: 50,
    height: 50,
  },
  lockedIconContainer: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#E5E7EB',
    justifyContent: 'center',
    alignItems: 'center',
  },
  lockedIcon: {
    fontSize: 24,
    color: '#9CA3AF',
  },
  checkmarkContainer: {
    position: 'absolute',
    top: -5,
    right: -5,
    width: 20,
    height: 20,
    borderRadius: 10,
    backgroundColor: '#4CAF50',
    justifyContent: 'center',
    alignItems: 'center',
  },
  checkmark: {
    fontSize: 12,
    color: '#fff',
  },
  medalName: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 5,
    textAlign: 'center',
    fontFamily: 'Poppins',
  },
  medalNameLocked: {
    color: '#9CA3AF',
  },
  medalDescription: {
    fontSize: 12,
    color: '#666',
    marginBottom: 8,
    textAlign: 'center',
    fontFamily: 'Poppins',
  },
  medalDescriptionLocked: {
    color: '#9CA3AF',
  },
  medalFooter: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 8,
  },
  medalCategory: {
    fontSize: 10,
    color: '#666',
    fontFamily: 'Poppins',
  },
  medalCategoryLocked: {
    color: '#9CA3AF',
  },
  medalRarity: {
    fontSize: 10,
    color: '#666',
    fontFamily: 'Poppins',
  },
  medalRarityLocked: {
    color: '#9CA3AF',
  },
  medalStatus: {
    alignItems: 'center',
  },
  medalStatusText: {
    fontSize: 12,
    color: '#4CAF50',
    fontWeight: '600',
    fontFamily: 'Poppins',
  },
  medalStatusTextLocked: {
    color: '#9CA3AF',
  },
});

export default MedalsScreen;
