import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Alert,
  SafeAreaView,
  TextInput,
  Modal,
} from 'react-native';
import useResponsive from '../../hooks/useResponsive';
import useCustomAlert from '../../hooks/useCustomAlert';
import CustomAlert from '../../components/CustomAlert';
import apiService from '../../services/apiService';

const AttendanceListScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  const { isMobile, isTablet, isDesktop, getPadding, getMargin, getFontSize, getSpacing } = useResponsive();
  const { alert, showSuccess, showError, hideAlert } = useCustomAlert();
  
  const [classData, setClassData] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendanceData, setAttendanceData] = useState({});
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [loading, setLoading] = useState(false);
  const [attendanceTaken, setAttendanceTaken] = useState(false);

  useEffect(() => {
    // Obter dados da turma dos par√¢metros de navega√ß√£o
    const params = window.navigationParams;
    if (params && params.classData) {
      setClassData(params.classData);
      loadStudents();
    }
  }, []);

  const loadStudents = async () => {
    if (!classData?.classId) return;
    
    try {
      setLoading(true);
      const response = await apiService.getClassStudents(classData.classId);
      
      if (response.success) {
        setStudents(response.data);
        // Inicializar todos como presentes por padr√£o
        const initialAttendance = {};
        response.data.forEach(student => {
          initialAttendance[student.id] = 'present';
        });
        setAttendanceData(initialAttendance);
      } else {
        showError('‚ùå Erro', 'Erro ao carregar alunos da turma');
      }
    } catch (error) {
      console.error('Erro ao carregar alunos:', error);
      showError('‚ùå Erro', 'Erro ao carregar alunos da turma');
    } finally {
      setLoading(false);
    }
  };

  const loadAttendanceForDate = async (date) => {
    if (!classData?.classId) return;
    
    try {
      setLoading(true);
      const response = await apiService.getClassAttendance(classData.classId, date);
      
      if (response.success) {
        const attendanceList = response.data.attendanceList || [];
        const newAttendanceData = {};
        
        students.forEach(student => {
          const attendance = attendanceList.find(a => a.student.id === student.id);
          if (attendance) {
            newAttendanceData[student.id] = attendance.isPresent ? 'present' : 'absent';
          } else {
            newAttendanceData[student.id] = 'present'; // Default
          }
        });
        
        setAttendanceData(newAttendanceData);
        setAttendanceTaken(attendanceList.length > 0);
      }
    } catch (error) {
      console.error('Erro ao carregar presen√ßas:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setShowDatePicker(false);
    loadAttendanceForDate(date);
  };

  const toggleAttendance = (studentId) => {
    setAttendanceData(prev => ({
      ...prev,
      [studentId]: prev[studentId] === 'present' ? 'absent' : 'present'
    }));
  };

  const takeAttendance = async () => {
    try {
      setLoading(true);
      
      if (!classData?.classId) {
        showError('‚ùå Erro', 'Aula n√£o est√° associada a uma turma');
        return;
      }

      // Preparar dados de presen√ßa
      const attendances = students.map(student => ({
        studentId: student.id,
        isPresent: attendanceData[student.id] === 'present',
        notes: null
      }));

      const response = await apiService.saveBatchAttendance(classData.classId, attendances, selectedDate);
      
      if (response.success) {
        setAttendanceTaken(true);
        showSuccess('Sucesso! üéâ', response.data.summary ? 
          `Chamada realizada: ${response.data.summary.present} presentes, ${response.data.summary.absent} faltas` :
          'Chamada realizada com sucesso!'
        );
      } else {
        throw new Error(response.message || 'Erro ao realizar chamada');
      }
    } catch (error) {
      console.error('Erro ao realizar chamada:', error);
      showError('‚ùå Erro', error.message || 'Erro ao realizar chamada');
    } finally {
      setLoading(false);
    }
  };

  const presentCount = Object.values(attendanceData).filter(status => status === 'present').length;
  const absentCount = Object.values(attendanceData).filter(status => status === 'absent').length;

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Header */}
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => onNavigate('teacherClasses')}
          >
            <Text style={styles.backButtonText}>‚Üê Voltar</Text>
          </TouchableOpacity>
          
          <Text style={[styles.title, { fontSize: getFontSize(24, 28, 32) }]}>
            Lista de Presen√ßa
          </Text>
          
          {classData && (
            <View style={styles.classInfo}>
              <Text style={[styles.className, { fontSize: getFontSize(18, 20, 22) }]}>
                {classData.name}
              </Text>
              <Text style={[styles.classDetails, { fontSize: getFontSize(14, 16, 18) }]}>
                {classData.school} - {classData.grade}
              </Text>
            </View>
          )}
        </View>

        {/* Date Selector */}
        <View style={styles.dateSection}>
          <Text style={[styles.sectionTitle, { fontSize: getFontSize(16, 18, 20) }]}>
            Data da Aula:
          </Text>
          
          <TouchableOpacity
            style={styles.dateButton}
            onPress={() => setShowDatePicker(true)}
          >
            <Text style={[styles.dateText, { fontSize: getFontSize(16, 18, 20) }]}>
              {new Date(selectedDate).toLocaleDateString('pt-BR')}
            </Text>
            <Text style={styles.dateButtonText}>üìÖ</Text>
          </TouchableOpacity>
        </View>

        {/* Summary */}
        <View style={styles.summarySection}>
          <View style={styles.summaryCard}>
            <Text style={[styles.summaryTitle, { fontSize: getFontSize(16, 18, 20) }]}>
              Resumo da Chamada
            </Text>
            <View style={styles.summaryStats}>
              <View style={styles.statItem}>
                <Text style={[styles.statNumber, { fontSize: getFontSize(24, 28, 32) }]}>
                  {presentCount}
                </Text>
                <Text style={[styles.statLabel, { fontSize: getFontSize(14, 16, 18) }]}>
                  Presentes
                </Text>
              </View>
              <View style={styles.statItem}>
                <Text style={[styles.statNumber, { fontSize: getFontSize(24, 28, 32) }]}>
                  {absentCount}
                </Text>
                <Text style={[styles.statLabel, { fontSize: getFontSize(14, 16, 18) }]}>
                  Faltas
                </Text>
              </View>
              <View style={styles.statItem}>
                <Text style={[styles.statNumber, { fontSize: getFontSize(24, 28, 32) }]}>
                  {students.length}
                </Text>
                <Text style={[styles.statLabel, { fontSize: getFontSize(14, 16, 18) }]}>
                  Total
                </Text>
              </View>
            </View>
          </View>
        </View>

        {/* Students List */}
        <View style={styles.studentsSection}>
          <Text style={[styles.sectionTitle, { fontSize: getFontSize(16, 18, 20) }]}>
            Alunos ({students.length})
          </Text>
          
          {students.map((student) => (
            <TouchableOpacity
              key={student.id}
              style={[
                styles.studentCard,
                attendanceData[student.id] === 'absent' && styles.studentCardAbsent,
                { padding: getPadding(12, 16, 20) }
              ]}
              onPress={() => toggleAttendance(student.id)}
            >
              <View style={styles.studentInfo}>
                <View style={styles.studentAvatar}>
                  <Text style={[styles.avatarText, { fontSize: getFontSize(16, 18, 20) }]}>
                    {student.name.charAt(0).toUpperCase()}
                  </Text>
                </View>
                <View style={styles.studentDetails}>
                  <Text style={[styles.studentName, { fontSize: getFontSize(16, 18, 20) }]}>
                    {student.name}
                  </Text>
                  <Text style={[styles.studentEmail, { fontSize: getFontSize(12, 14, 16) }]}>
                    {student.email}
                  </Text>
                </View>
              </View>
              
              <View style={styles.attendanceStatus}>
                {attendanceData[student.id] === 'present' ? (
                  <Text style={[styles.statusPresent, { fontSize: getFontSize(14, 16, 18) }]}>
                    ‚úÖ Presente
                  </Text>
                ) : (
                  <Text style={[styles.statusAbsent, { fontSize: getFontSize(14, 16, 18) }]}>
                    ‚ùå Falta
                  </Text>
                )}
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {/* Action Button */}
        <View style={styles.actionSection}>
          <TouchableOpacity
            style={[
              styles.takeAttendanceButton,
              loading && styles.takeAttendanceButtonDisabled,
              { padding: getPadding(16, 20, 24) }
            ]}
            onPress={takeAttendance}
            disabled={loading}
          >
            <Text style={[styles.takeAttendanceButtonText, { fontSize: getFontSize(16, 18, 20) }]}>
              {loading ? 'Salvando...' : attendanceTaken ? 'Atualizar Chamada' : 'Realizar Chamada'}
            </Text>
          </TouchableOpacity>
        </View>

        {/* Spacing */}
        <View style={{ height: getSpacing(20, 30, 40) }} />
      </ScrollView>

      {/* Date Picker Modal */}
      <Modal
        visible={showDatePicker}
        transparent={true}
        animationType="fade"
        onRequestClose={() => setShowDatePicker(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <Text style={[styles.modalTitle, { fontSize: getFontSize(18, 20, 22) }]}>
              Selecionar Data
            </Text>
            
            <TextInput
              style={[styles.dateInput, { fontSize: getFontSize(16, 18, 20) }]}
              value={selectedDate}
              onChangeText={setSelectedDate}
              placeholder="YYYY-MM-DD"
            />
            
            <View style={styles.modalButtons}>
              <TouchableOpacity
                style={styles.modalButton}
                onPress={() => setShowDatePicker(false)}
              >
                <Text style={[styles.modalButtonText, { fontSize: getFontSize(14, 16, 18) }]}>
                  Cancelar
                </Text>
              </TouchableOpacity>
              
              <TouchableOpacity
                style={[styles.modalButton, styles.modalButtonPrimary]}
                onPress={() => handleDateChange(selectedDate)}
              >
                <Text style={[styles.modalButtonText, styles.modalButtonTextPrimary, { fontSize: getFontSize(14, 16, 18) }]}>
                  Confirmar
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>

      <CustomAlert
        visible={alert.visible}
        title={alert.title}
        message={alert.message}
        type={alert.type}
        onClose={hideAlert}
      />
    </SafeAreaView>
  );
};

