import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  SafeAreaView,
  Image,
  ActivityIndicator,
  RefreshControl,
} from 'react-native';
import SideMenu from '../../components/SideMenu';
import apiService from '../../services/apiService';

const { width, height } = Dimensions.get('window');

const AchievementsScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  const [profileData, setProfileData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadAchievementsData();
  }, []);

  const loadAchievementsData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Configurar token de autentica√ß√£o
      const token = localStorage.getItem('authToken');
      if (token) {
        apiService.setToken(token);
      } else {
        console.error('Token n√£o encontrado');
        setError('Token de autentica√ß√£o n√£o encontrado');
        return;
      }
      
      const response = await apiService.getStudentProfile();
      if (response.success) {
        setProfileData(response.data);
      } else {
        setError(response.message || 'Erro ao carregar conquistas');
      }
    } catch (err) {
      console.error('Erro ao carregar conquistas:', err);
      setError('N√£o foi poss√≠vel carregar as conquistas. Tente novamente.');
    } finally {
      setIsLoading(false);
      setRefreshing(false);
    }
  };

  const onRefresh = () => {
    setRefreshing(true);
    loadAchievementsData();
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'common': return '#6B7280';
      case 'rare': return '#3B82F6';
      case 'epic': return '#8B5CF6';
      case 'legendary': return '#F59E0B';
      default: return '#6B7280';
    }
  };

  const getRarityIcon = (rarity) => {
    switch (rarity) {
      case 'common': return '‚ö™';
      case 'rare': return 'üîµ';
      case 'epic': return 'üü£';
      case 'legendary': return 'üü°';
      default: return '‚ö™';
    }
  };

  const isAchievementUnlocked = (achievement) => {
    if (!profileData) return false;
    return profileData.achievements.unlocked.some(unlockedAchievement => unlockedAchievement.id === achievement.id);
  };

  const renderAchievementCard = (achievement) => {
    const isUnlocked = isAchievementUnlocked(achievement);
    
    return (
      <View key={achievement.id} style={[
        styles.achievementCard,
        isUnlocked && styles.achievementCardUnlocked
      ]}>
        <View style={styles.achievementHeader}>
          <View style={[
            styles.achievementIconContainer,
            { backgroundColor: isUnlocked ? achievement.color : '#E5E7EB' }
          ]}>
            {isUnlocked ? (
              <Image 
                source={{ uri: achievement.icon }} 
                style={styles.achievementIcon}
                resizeMode="contain"
              />
            ) : (
              <Text style={styles.lockedIcon}>üîí</Text>
            )}
          </View>
          
          <View style={[
            styles.rarityBadge,
            { backgroundColor: getRarityColor(achievement.rarity) }
          ]}>
            <Text style={styles.rarityText}>
              {getRarityIcon(achievement.rarity)}
            </Text>
          </View>
        </View>

        <Text style={[
          styles.achievementName,
          !isUnlocked && styles.achievementNameLocked
        ]}>
          {achievement.name}
        </Text>

        <Text style={[
          styles.achievementDescription,
          !isUnlocked && styles.achievementDescriptionLocked
        ]}>
          {achievement.description}
        </Text>

        <View style={styles.requirementContainer}>
          <Text style={[
            styles.requirementText,
            !isUnlocked && styles.requirementTextLocked
          ]}>
            {isUnlocked ? '‚úÖ Desbloqueada' : `üîí ${achievement.requirement} exerc√≠cios`}
          </Text>
        </View>

        <View style={styles.categoryContainer}>
          <Text style={[
            styles.categoryText,
            !isUnlocked && styles.categoryTextLocked
          ]}>
            {achievement.category}
          </Text>
          <Text style={[
            styles.rarityText,
            !isUnlocked && styles.rarityTextLocked
          ]}>
            {achievement.rarity}
          </Text>
        </View>
      </View>
    );
  };

  const renderHeader = () => (
    <View style={styles.header}>
      <Text style={styles.title}>üèÜ Conquistas</Text>
      {profileData && (
        <Text style={styles.subtitle}>
          {profileData.achievements.stats.unlocked} de {profileData.achievements.stats.total} conquistas obtidas
        </Text>
      )}
    </View>
  );

  const renderProgressBar = () => {
    if (!profileData) return null;
    
    const progress = (profileData.achievements.stats.unlocked / profileData.achievements.stats.total) * 100;
    
    return (
      <View style={styles.progressContainer}>
        <View style={styles.progressBar}>
          <View style={[styles.progressFill, { width: `${progress}%` }]} />
        </View>
        <Text style={styles.progressText}>
          {Math.round(progress)}% completo
        </Text>
      </View>
    );
  };

  if (isLoading) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#F9BB55" />
          <Text style={styles.loadingText}>Carregando conquistas...</Text>
        </View>
      </SafeAreaView>
    );
  }

  if (error) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
          <TouchableOpacity style={styles.retryButton} onPress={loadAchievementsData}>
            <Text style={styles.retryButtonText}>Tentar Novamente</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} colors={['#F9BB55']} />
        }
      >
        {renderHeader()}
        {renderProgressBar()}
        
        <View style={styles.achievementsGrid}>
          {profileData?.achievements?.all?.map(achievement => renderAchievementCard(achievement))}
        </View>
      </ScrollView>
      
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
        currentUser={currentUser}
        onLogout={onLogout}
        userType="STUDENT"
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EDED',
  },
  scrollView: {
    flex: 1,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
    fontFamily: 'Poppins',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  errorText: {
    fontSize: 16,
    color: '#D9493C',
    textAlign: 'center',
    marginBottom: 20,
    fontFamily: 'Poppins',
  },
  retryButton: {
    backgroundColor: '#F9BB55',
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 8,
  },
  retryButtonText: {
    color: '#000',
    fontWeight: 'bold',
    fontFamily: 'Poppins',
  },
  header: {
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 10,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    marginTop: 5,
    fontFamily: 'Poppins',
  },
  progressContainer: {
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  progressBar: {
    height: 8,
    backgroundColor: '#E5E7EB',
    borderRadius: 4,
    overflow: 'hidden',
  },
  progressFill: {
    height: '100%',
    backgroundColor: '#F9BB55',
    borderRadius: 4,
  },
  progressText: {
    textAlign: 'center',
    marginTop: 8,
    fontSize: 14,
    color: '#666',
    fontFamily: 'Poppins',
  },
  achievementsGrid: {
    paddingHorizontal: 20,
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  achievementCard: {
    width: (width - 60) / 2,
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 15,
    marginBottom: 15,
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    elevation: 3,
  },
  achievementCardUnlocked: {
    borderWidth: 2,
    borderColor: '#F9BB55',
  },
  achievementHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  achievementIconContainer: {
    width: 50,
    height: 50,
    borderRadius: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
  achievementIcon: {
    width: 40,
    height: 40,
  },
  lockedIcon: {
    fontSize: 24,
    color: '#9CA3AF',
  },
  rarityBadge: {
    width: 24,
    height: 24,
    borderRadius: 12,
    justifyContent: 'center',
    alignItems: 'center',
  },
  rarityText: {
    fontSize: 12,
    color: '#fff',
  },
  achievementName: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 5,
    fontFamily: 'Poppins',
  },
  achievementNameLocked: {
    color: '#9CA3AF',
  },
  achievementDescription: {
    fontSize: 12,
    color: '#666',
    marginBottom: 10,
    fontFamily: 'Poppins',
  },
  achievementDescriptionLocked: {
    color: '#9CA3AF',
  },
  requirementContainer: {
    marginBottom: 10,
  },
  requirementText: {
    fontSize: 12,
    color: '#059669',
    fontWeight: '600',
    fontFamily: 'Poppins',
  },
  requirementTextLocked: {
    color: '#9CA3AF',
