import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  TextInput,
  Dimensions,
  SafeAreaView,
  Image,
  Alert,
} from 'react-native';
import CustomAlert from './CustomAlert';
import useCustomAlert from './useCustomAlert';
import SideMenu from './SideMenu';

const { width, height } = Dimensions.get('window');

const RegisterScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, onRegister, onNavigateToLogin }) => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    school: '',
    class: '',
    email: '',
    password: '',
    cpf: '',
  });
  const [isLoading, setIsLoading] = useState(false);
  const { alert, showSuccess, showError, hideAlert } = useCustomAlert();
  const [fieldErrors, setFieldErrors] = useState({});

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    
    // Limpar erro do campo quando o usu√°rio come√ßar a digitar
    if (fieldErrors[field]) {
      setFieldErrors(prev => ({
        ...prev,
        [field]: null
      }));
    }
  };

  // Fun√ß√£o para validar CPF
  const isValidCPF = (cpf) => {
    // Remove caracteres n√£o num√©ricos
    cpf = cpf.replace(/\D/g, '');
    
    // Verifica se tem 11 d√≠gitos
    if (cpf.length !== 11) return false;
    
    // Verifica se todos os d√≠gitos s√£o iguais
    if (/^(\d)\1{10}$/.test(cpf)) return false;
    
    // Valida√ß√£o do primeiro d√≠gito verificador
    let sum = 0;
    for (let i = 0; i < 9; i++) {
      sum += parseInt(cpf.charAt(i)) * (10 - i);
    }
    let remainder = (sum * 10) % 11;
    if (remainder === 10 || remainder === 11) remainder = 0;
    if (remainder !== parseInt(cpf.charAt(9))) return false;
    
    // Valida√ß√£o do segundo d√≠gito verificador
    sum = 0;
    for (let i = 0; i < 10; i++) {
      sum += parseInt(cpf.charAt(i)) * (11 - i);
    }
    remainder = (sum * 10) % 11;
    if (remainder === 10 || remainder === 11) remainder = 0;
    if (remainder !== parseInt(cpf.charAt(10))) return false;
    
    return true;
  };

  const validateForm = () => {
    const errors = {};
    
    // Validar nome
    if (!formData.name.trim()) {
      errors.name = 'Nome √© obrigat√≥rio';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Nome deve ter pelo menos 2 caracteres';
    }
    
    // Validar idade
    if (!formData.age.trim()) {
      errors.age = 'Idade √© obrigat√≥ria';
    } else {
      const age = parseInt(formData.age, 10);
      if (isNaN(age) || age < 1 || age > 120) {
        errors.age = 'Idade deve ser entre 1 e 120 anos';
      }
    }
    
    // Validar escola
    if (!formData.school.trim()) {
      errors.school = 'Escola √© obrigat√≥ria';
    } else if (formData.school.trim().length < 2) {
      errors.school = 'Nome da escola deve ter pelo menos 2 caracteres';
    }
    
    // Validar turma
    if (!formData.class.trim()) {
      errors.class = 'Turma √© obrigat√≥ria';
    } else if (formData.class.trim().length < 1) {
      errors.class = 'Turma deve ter pelo menos 1 caractere';
    }
    
    // Validar email
    if (!formData.email.trim()) {
      errors.email = 'E-mail √© obrigat√≥rio';
    } else {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(formData.email.trim())) {
        errors.email = 'E-mail inv√°lido (exemplo: usuario@email.com)';
      }
    }
    
    // Validar senha
    if (!formData.password.trim()) {
      errors.password = 'Senha √© obrigat√≥ria';
    } else if (formData.password.length < 6) {
      errors.password = 'Senha deve ter pelo menos 6 caracteres';
    } else if (formData.password.length > 50) {
      errors.password = 'Senha deve ter no m√°ximo 50 caracteres';
    } else {
      const hasLetter = /[a-zA-Z]/.test(formData.password);
      const hasNumber = /[0-9]/.test(formData.password);
      
      if (!hasLetter || !hasNumber) {
        errors.password = 'Senha deve conter pelo menos uma letra e um n√∫mero';
      }
    }
    
    // Validar CPF
    if (!formData.cpf.trim()) {
      errors.cpf = 'CPF √© obrigat√≥rio';
    } else {
      const cpf = formData.cpf.replace(/\D/g, ''); // Remove caracteres n√£o num√©ricos
      if (cpf.length !== 11) {
        errors.cpf = 'CPF deve ter 11 d√≠gitos';
      } else if (!isValidCPF(cpf)) {
        errors.cpf = 'CPF inv√°lido';
      }
    }
    
    // Definir erros
    setFieldErrors(errors);
    
    // Se h√° erros, mostrar o primeiro
    if (Object.keys(errors).length > 0) {
      const firstError = Object.values(errors)[0];
      showError('‚ùå Dados inv√°lidos', firstError);
      return false;
    }
    
    return true;
  };

  const handleSave = async () => {
    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      // Converter age para n√∫mero
      const userDataToSend = {
        ...formData,
        age: parseInt(formData.age, 10)
      };
      
      const result = await onRegister(userDataToSend);
      
      if (result && result.success) {
        // Usar Alert para web
        if (typeof window !== 'undefined' && window.alert) {
          window.alert(`Sucesso: ${result.message}`);
        } else {
          showSuccess('Sucesso! üéâ', result.message);
          setTimeout(() => {
            onNavigateToLogin && onNavigateToLogin();
          }, 2000);
        }
        
        // Redirecionar automaticamente ap√≥s 2 segundos
        setTimeout(() => {
          onNavigateToLogin && onNavigateToLogin();
        }, 2000);
        
      } else {
        if (typeof window !== 'undefined' && window.alert) {
          window.alert(`Erro: Resposta inesperada do servidor - ${JSON.stringify(result)}`);
        } else {
          showError('‚ùå Erro', 'Resposta inesperada do servidor');
        }
      }
    } catch (error) {
      console.error('Erro no cadastro:', error);
      if (typeof window !== 'undefined' && window.alert) {
        window.alert(`Erro: ${error.message || 'Erro ao salvar cadastro'}`);
      } else {
        showError('‚ùå Erro', error.message || 'Erro ao salvar cadastro');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleProfileImagePress = () => {
    // Placeholder function - will be implemented later
    alert('Selecionar foto de perfil (Funcionalidade ser√° implementada)');
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.logo}>Movz</Text>
          <TouchableOpacity 
            style={styles.menuIcon} 
            onPress={() => setIsMenuVisible(true)}
          >
            <View style={styles.menuLine} />
            <View style={styles.menuLine} />
            <View style={styles.menuLine} />
          </TouchableOpacity>
        </View>

        {/* Title */}
        <Text style={styles.title}>Fa√ßa seu cadastro</Text>

        {/* Profile Image Section */}
        <View style={styles.profileImageContainer}>
          <TouchableOpacity 
            style={styles.profileImagePlaceholder}
            onPress={handleProfileImagePress}
          >
            <View style={styles.profileImageCircle}>
              <Text style={styles.profileImageText}>+</Text>
            </View>
          </TouchableOpacity>
          <Text style={styles.profileImageLabel}>Escolher foto de perfil</Text>
        </View>

        {/* Form Fields */}
        <View style={styles.formContainer}>
          {/* Nome */}
          <View style={styles.inputContainer}>
            <TextInput
              style={[styles.input, fieldErrors.name && styles.inputError]}
              placeholder="Nome"
              placeholderTextColor="#666"
              value={formData.name}
              onChangeText={(value) => handleInputChange('name', value)}
            />
            {fieldErrors.name && <Text style={styles.errorText}>{fieldErrors.name}</Text>}
          </View>

          {/* Idade */}
          <View style={styles.inputContainer}>
            <TextInput
              style={[styles.input, fieldErrors.age && styles.inputError]}
              placeholder="Idade"
              placeholderTextColor="#666"
              value={formData.age}
              onChangeText={(value) => handleInputChange('age', value)}
              keyboardType="numeric"
            />
            {fieldErrors.age && <Text style={styles.errorText}>{fieldErrors.age}</Text>}
          </View>

          {/* Escola */}
          <View style={styles.inputContainer}>
            <TextInput
              style={[styles.input, fieldErrors.school && styles.inputError]}
              placeholder="Escola"
              placeholderTextColor="#666"
              value={formData.school}
              onChangeText={(value) => handleInputChange('school', value)}
            />
            {fieldErrors.school && <Text style={styles.errorText}>{fieldErrors.school}</Text>}
          </View>

          {/* Turma */}
          <View style={styles.inputContainer}>
            <TextInput
              style={[styles.input, fieldErrors.class && styles.inputError]}
              placeholder="Turma"
              placeholderTextColor="#666"
              value={formData.class}
              onChangeText={(value) => handleInputChange('class', value)}
            />
            {fieldErrors.class && <Text style={styles.errorText}>{fieldErrors.class}</Text>}
          </View>

          {/* E-mail */}
          <View style={styles.inputContainer}>
            <TextInput
              style={[styles.input, fieldErrors.email && styles.inputError]}
              placeholder="E-mail"
              placeholderTextColor="#666"
              value={formData.email}
              onChangeText={(value) => handleInputChange('email', value)}
              keyboardType="email-address"
              autoCapitalize="none"
            />
            {fieldErrors.email && <Text style={styles.errorText}>{fieldErrors.email}</Text>}
          </View>

          {/* Senha */}
          <View style={styles.inputContainer}>
            <TextInput
              style={[styles.input, fieldErrors.password && styles.inputError]}
              placeholder="Senha"
              placeholderTextColor="#666"
              value={formData.password}
              onChangeText={(value) => handleInputChange('password', value)}
              secureTextEntry
            />
            {fieldErrors.password && <Text style={styles.errorText}>{fieldErrors.password}</Text>}
          </View>
        </View>

        {/* Save Button */}
        <TouchableOpacity 
          style={[styles.saveButton, isLoading && styles.saveButtonDisabled]} 
          onPress={handleSave}
          disabled={isLoading}
        >
          <Text style={styles.saveButtonText}>
            {isLoading ? 'Salvando...' : 'Salvar'}
          </Text>
        </TouchableOpacity>
      </ScrollView>
      
      {/* Side Menu */}
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
      />
      
      <CustomAlert
        visible={alert.visible}
        title={alert.title}
        message={alert.message}
        type={alert.type}
        onClose={hideAlert}
      />
    </SafeAreaView>
  );
};

