import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Dimensions,
  Animated,
  Image,
} from 'react-native';

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

const SideMenu = ({ isVisible, onClose }) => {
  const slideAnim = React.useRef(new Animated.Value(screenWidth)).current;

  React.useEffect(() => {
    if (isVisible) {
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 300,
        useNativeDriver: true,
      }).start();
    } else {
      Animated.timing(slideAnim, {
        toValue: screenWidth,
        duration: 300,
        useNativeDriver: true,
      }).start();
    }
  }, [isVisible, slideAnim]);

  if (!isVisible) return null;

  const menuItems = [
    { id: 'perfil', title: 'Perfil' },
    { id: 'ranking', title: 'Ranking' },
    { id: 'chat', title: 'Chat' },
    { id: 'tutorial', title: 'Tutorial' },
    { id: 'menu', title: 'Menu' },
  ];

  return (
    <View style={styles.overlay}>
      <TouchableOpacity style={styles.backdrop} onPress={onClose} />
      <Animated.View
        style={[
          styles.menuContainer,
          {
            transform: [{ translateX: slideAnim }],
          },
        ]}
      >
        {/* Noise Overlay */}
        <View style={styles.noiseOverlay} />
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.logo}>Movz</Text>
          <TouchableOpacity style={styles.closeButton} onPress={onClose}>
            <View style={styles.closeIcon}>
              <View style={[styles.closeLine, styles.closeLine1]} />
              <View style={[styles.closeLine, styles.closeLine2]} />
            </View>
          </TouchableOpacity>
        </View>

        {/* Hamburger lines */}
        <View style={styles.hamburgerLines}>
          <View style={styles.hamburgerLine} />
          <View style={styles.hamburgerLine} />
          <View style={styles.hamburgerLine} />
        </View>

        {/* User Profile Section */}
        <View style={styles.userSection}>
          <Image
            source={require('./img/aiAtivo 1logo.png')}
            style={styles.userAvatar}
            resizeMode="cover"
          />
          <Text style={styles.userGreeting}>Oi, Carolline</Text>
        </View>

        {/* Menu Items */}
        <View style={styles.menuItemsContainer}>
          {menuItems.map((item, index) => (
            <TouchableOpacity key={item.id} style={styles.menuItem}>
              <View style={styles.menuItemBorder} />
              <Text style={styles.menuItemText}>{item.title}</Text>
            </TouchableOpacity>
          ))}
        </View>

        {/* Content Area - Same as HomeScreen but in scrollable format */}
        <View style={styles.contentArea}>
          {/* XP Progress */}
          <View style={styles.xpSection}>
            <Text style={styles.xpTitle}>Seu XP até agora</Text>
            <Text style={styles.xpValue}>2.500</Text>
            <View style={styles.progressBar}>
              <View style={styles.progressFill} />
            </View>
          </View>

          {/* Sports Scores */}
          <View style={styles.sportsCard}>
            <Text style={styles.cardTitle}>Sua pontuação nos Esportes</Text>
            <View style={styles.sportsContainer}>
              {[
                { score: '90', label: 'Esporte' },
                { score: '100', label: 'Esporte' },
                { score: '30', label: 'Esporte' },
              ].map((sport, index) => (
                <View key={index} style={styles.sportItem}>
                  <View style={styles.sportScoreBox}>
                    <Text style={styles.sportScore}>{sport.score}</Text>
                  </View>
                  <Text style={styles.sportLabel}>{sport.label}</Text>
                </View>
              ))}
            </View>
          </View>

          {/* Attendance */}
          <View style={styles.attendanceCard}>
            <Text style={styles.cardTitle}>Presença nas aulas</Text>
            <View style={styles.attendanceDays}>
              {['seg', 'ter', 'qua', 'qui', 'sex'].map((day, index) => (
                <View key={day} style={styles.dayContainer}>
                  <View style={styles.dayCircle}>
                    {index < 2 && (
                      <View style={styles.checkIcon}>
                        <View style={styles.checkOuter} />
                        <View style={styles.checkInner} />
                      </View>
                    )}
                  </View>
                  <Text style={styles.dayText}>{day}</Text>
                </View>
              ))}
            </View>
            <Text style={styles.attendanceText}>
              Você está a 30 dias sem faltar as aulas
            </Text>
          </View>

          {/* Medals */}
          <View style={styles.medalsCard}>
            <Text style={styles.cardTitle}>Suas medalhas</Text>
            <View style={styles.medalBanner}>
              {[
                { month: 'Março', image: require('./img/Medalha_2.svg') },
                { month: 'Abril', image: require('./img/Medalha_4.svg') },
                { month: 'Maio', image: require('./img/Medalha_5.svg') },
              ].map((medal, index) => (
                <View key={index} style={styles.medalContainer}>
                  <Image source={medal.image} style={styles.medalIcon} resizeMode="contain" />
                  <Text style={styles.medalMonth}>{medal.month}</Text>
                </View>
              ))}
            </View>
            <TouchableOpacity style={styles.seeMoreButton}>
              <Text style={styles.seeMoreText}>Ver mais</Text>
            </TouchableOpacity>
          </View>

          {/* Achievements */}
          <View style={styles.achievementsCard}>
            <Text style={styles.cardTitle}>Suas conquistas</Text>
            <View style={styles.medalBanner}>
              {[
                { month: 'Março', image: require('./img/Medalha_1.svg') },
                { month: 'Abril', image: require('./img/Medalha_3.svg') },
                { month: 'Maio', image: require('./img/Medalha_6.svg') },
              ].map((achievement, index) => (
                <View key={index} style={styles.medalContainer}>
                  <Image source={achievement.image} style={styles.medalIcon} resizeMode="contain" />
                  <Text style={styles.medalMonth}>{achievement.month}</Text>
                </View>
              ))}
            </View>
            <TouchableOpacity style={styles.seeMoreButton}>
              <Text style={styles.seeMoreText}>Ver mais</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 1000,
  },
  backdrop: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  menuContainer: {
    position: 'absolute',
    top: 0,
    right: 0,
    width: screenWidth * 0.8,
    height: screenHeight,
    backgroundColor: 'rgba(33, 125, 137, 0.9)',
    borderLeftWidth: 1,
    borderLeftColor: 'rgba(255, 255, 255, 0.34)',
    shadowColor: '#000',
    shadowOffset: { width: -5, height: 0 },
    shadowOpacity: 0.3,
    shadowRadius: 10,
    elevation: 10,
    // Background blur effect
    backdropFilter: 'blur(4px)',
    // Noise texture overlay
    overflow: 'hidden',
  },
  noiseOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
