import React, { useState, useEffect } from 'react';
import { StatusBar } from 'expo-status-bar';
import AsyncStorage from '@react-native-async-storage/async-storage';
import LoginScreen from './LoginScreen';
import HomeScreen from './HomeScreen';
import RankingScreen from './RankingScreen';
import TutorialScreen from './TutorialScreen';
import ChatScreen from './ChatScreen';
import RegisterScreen from './RegisterScreen';

export default function App() {
  const [currentScreen, setCurrentScreen] = useState('home');
  const [isMenuVisible, setIsMenuVisible] = useState(false);

  const navigateToScreen = (screen) => {
    setCurrentScreen(screen);
    setIsMenuVisible(false);
  };

  const renderScreen = () => {
    switch (currentScreen) {
      case 'ranking':
        return <RankingScreen isMenuVisible={isMenuVisible} setIsMenuVisible={setIsMenuVisible} onNavigate={navigateToScreen} />;
      case 'tutorial':
        return <TutorialScreen isMenuVisible={isMenuVisible} setIsMenuVisible={setIsMenuVisible} onNavigate={navigateToScreen} />;
      case 'chat':
        return <ChatScreen isMenuVisible={isMenuVisible} setIsMenuVisible={setIsMenuVisible} onNavigate={navigateToScreen} />;
      case 'register':
        return <RegisterScreen isMenuVisible={isMenuVisible} setIsMenuVisible={setIsMenuVisible} onNavigate={navigateToScreen} />;
      default:
        return <HomeScreen isMenuVisible={isMenuVisible} setIsMenuVisible={setIsMenuVisible} onNavigate={navigateToScreen} />;
    }
  };

  return (
    <>
      <StatusBar style="dark" backgroundColor="#E9EEEE" />
      {renderScreen()}
    </>
  );
}
