import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  TextInput,
  Dimensions,
  SafeAreaView,
  Image,
  Alert,
} from 'react-native';
import Storage from './storage';
import SideMenu from './SideMenu';

const { width, height } = Dimensions.get('window');

const RegisterScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, onRegister }) => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    school: '',
    class: '',
    email: '',
    password: '',
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      Alert.alert('Erro', 'Por favor, informe seu nome');
      return false;
    }
    if (!formData.age.trim()) {
      Alert.alert('Erro', 'Por favor, informe sua idade');
      return false;
    }
    if (!formData.school.trim()) {
      Alert.alert('Erro', 'Por favor, informe sua escola');
      return false;
    }
    if (!formData.class.trim()) {
      Alert.alert('Erro', 'Por favor, informe sua turma');
      return false;
    }
    if (!formData.email.trim()) {
      Alert.alert('Erro', 'Por favor, informe seu e-mail');
      return false;
    }
    if (!formData.password.trim()) {
      Alert.alert('Erro', 'Por favor, informe sua senha');
      return false;
    }
    if (formData.password.length < 6) {
      Alert.alert('Erro', 'A senha deve ter pelo menos 6 caracteres');
      return false;
    }
    return true;
  };

  const handleSave = async () => {
    if (!validateForm()) return;

    setIsLoading(true);

    try {
      // Buscar usuários existentes
      const usersJson = await AsyncStorage.getItem('users');
      const users = usersJson ? JSON.parse(usersJson) : [];

      // Verificar se o email já existe
      const existingUser = users.find(u => u.email === formData.email);
      if (existingUser) {
        Alert.alert('Erro', 'Este e-mail já está cadastrado');
        setIsLoading(false);
        return;
      }

      // Criar novo usuário
      const newUser = {
        id: Date.now().toString(),
        ...formData,
        createdAt: new Date().toISOString(),
      };

      // Salvar usuário
      users.push(newUser);
      await AsyncStorage.setItem('users', JSON.stringify(users));

      // Salvar como usuário atual
      await AsyncStorage.setItem('currentUser', JSON.stringify(newUser));

      Alert.alert('Sucesso', 'Cadastro realizado com sucesso!', [
        {
          text: 'OK',
          onPress: () => onRegister(newUser)
        }
      ]);

    } catch (error) {
      Alert.alert('Erro', 'Erro ao salvar cadastro');
      console.error('Register error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleProfileImagePress = () => {
    // Placeholder function - will be implemented later
    alert('Selecionar foto de perfil (Funcionalidade será implementada)');
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.logo}>Movz</Text>
          <TouchableOpacity 
            style={styles.menuIcon} 
            onPress={() => setIsMenuVisible(true)}
          >
            <View style={styles.menuLine} />
            <View style={styles.menuLine} />
            <View style={styles.menuLine} />
          </TouchableOpacity>
        </View>

        {/* Title */}
        <Text style={styles.title}>Faça seu cadastro</Text>

        {/* Profile Image Section */}
        <View style={styles.profileImageContainer}>
          <TouchableOpacity 
            style={styles.profileImagePlaceholder}
            onPress={handleProfileImagePress}
          >
            <View style={styles.profileImageCircle}>
              <Text style={styles.profileImageText}>+</Text>
            </View>
          </TouchableOpacity>
          <Text style={styles.profileImageLabel}>Escolher foto de perfil</Text>
        </View>

        {/* Form Fields */}
        <View style={styles.formContainer}>
          {/* Nome */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Nome"
              placeholderTextColor="#666"
              value={formData.name}
              onChangeText={(value) => handleInputChange('name', value)}
            />
          </View>

          {/* Idade */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Idade"
              placeholderTextColor="#666"
              value={formData.age}
              onChangeText={(value) => handleInputChange('age', value)}
              keyboardType="numeric"
            />
          </View>

          {/* Escola */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Escola"
              placeholderTextColor="#666"
              value={formData.school}
              onChangeText={(value) => handleInputChange('school', value)}
            />
          </View>

          {/* Turma */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Turma"
              placeholderTextColor="#666"
              value={formData.class}
              onChangeText={(value) => handleInputChange('class', value)}
            />
          </View>

          {/* E-mail */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="E-mail"
              placeholderTextColor="#666"
              value={formData.email}
              onChangeText={(value) => handleInputChange('email', value)}
              keyboardType="email-address"
              autoCapitalize="none"
            />
          </View>

          {/* Senha */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Senha"
              placeholderTextColor="#666"
              value={formData.password}
              onChangeText={(value) => handleInputChange('password', value)}
              secureTextEntry
            />
          </View>
        </View>

        {/* Save Button */}
        <TouchableOpacity 
          style={[styles.saveButton, isLoading && styles.saveButtonDisabled]} 
          onPress={handleSave}
          disabled={isLoading}
        >
          <Text style={styles.saveButtonText}>
            {isLoading ? 'Salvando...' : 'Salvar'}
          </Text>
        </TouchableOpacity>
      </ScrollView>
      
      {/* Side Menu */}
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  scrollView: {
    flex: 1,
    paddingHorizontal: 20,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingTop: 20,
    paddingBottom: 20,
  },
  logo: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
  },
  menuIcon: {
    width: 39,
    height: 18,
    justifyContent: 'space-between',
  },
  menuLine: {
    width: 39,
    height: 6,
    backgroundColor: '#D9D9D9',
    borderRadius: 3,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#000',
    textAlign: 'center',
    marginBottom: 30,
    fontFamily: 'Poppins',
  },
  profileImageContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  profileImagePlaceholder: {
    marginBottom: 15,
  },
  profileImageCircle: {
    width: 161,
    height: 161,
    borderRadius: 80.5,
    backgroundColor: '#D9D9D9',
    justifyContent: 'center',
    alignItems: 'center',
  },
  profileImageText: {
    fontSize: 48,
    color: '#666',
    fontWeight: 'bold',
  },
  profileImageLabel: {
    fontSize: 16,
    color: '#000',
    fontFamily: 'Poppins',
  },
  formContainer: {
    marginBottom: 30,
  },
  inputContainer: {
    marginBottom: 15,
  },
  input: {
    backgroundColor: '#D9D9D9',
    height: 43,
    paddingHorizontal: 20,
    fontSize: 16,
    color: '#000',
    fontFamily: 'Poppins',
    textAlign: 'center',
  },
  saveButton: {
    backgroundColor: '#B5B5B5',
    height: 43,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 30,
    alignSelf: 'center',
    width: 233,
  },
  saveButtonText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
  },
  saveButtonDisabled: {
    backgroundColor: '#D9D9D9',
  },
});

export default RegisterScreen;
