import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  SafeAreaView,
  Image,
  ActivityIndicator,
  RefreshControl,
} from 'react-native';
import SideMenu from '../../components/SideMenu';
import apiService from '../../services/apiService';

const { width, height } = Dimensions.get('window');

const AchievementsScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  const [profileData, setProfileData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadAchievementsData();
  }, []);

  const loadAchievementsData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Configurar token de autenticação
      const token = localStorage.getItem('authToken');
      if (token) {
        apiService.setToken(token);
      } else {
        console.error('Token não encontrado');
        setError('Token de autenticação não encontrado');
        return;
      }
      
      const response = await apiService.getStudentProfile();
      if (response.success) {
        setProfileData(response.data);
      } else {
        setError(response.message || 'Erro ao carregar conquistas');
      }
    } catch (err) {
      console.error('Erro ao carregar conquistas:', err);
      setError('Não foi possível carregar as conquistas. Tente novamente.');
    } finally {
      setIsLoading(false);
      setRefreshing(false);
    }
  };

  const onRefresh = () => {
    setRefreshing(true);
    loadAchievementsData();
  };

  const isAchievementObtained = (achievementId) => {
    return userAchievements.includes(achievementId);
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Comum': return '#95A5A6';
      case 'Rara': return '#3498DB';
      case 'Épica': return '#9B59B6';
      case 'Lendária': return '#F39C12';
      case 'Mítica': return '#E74C3C';
      default: return '#95A5A6';
    }
  };

  const renderAchievementCard = (achievement) => {
    const obtained = isAchievementObtained(achievement.id);
    
    return (
      <View key={achievement.id} style={[styles.achievementCard, obtained && styles.achievementCardObtained]}>
        <View style={styles.achievementIconContainer}>
          <Image 
            source={achievement.icon} 
            style={[styles.achievementIcon, !obtained && styles.achievementIconLocked]} 
            resizeMode="contain"
          />
          {obtained && (
            <View style={styles.obtainedBadge}>
              <Text style={styles.obtainedText}>✓</Text>
            </View>
          )}
        </View>
        
        <View style={styles.achievementInfo}>
          <Text style={[styles.achievementName, !obtained && styles.achievementNameLocked]}>
            {achievement.name}
          </Text>
          <Text style={[styles.achievementDescription, !obtained && styles.achievementDescriptionLocked]}>
            {achievement.description}
          </Text>
          <Text style={[styles.achievementRequirement, !obtained && styles.achievementRequirementLocked]}>
            {achievement.requirement}
          </Text>
          
          <View style={styles.achievementTags}>
            <View style={[styles.achievementTag, { backgroundColor: achievement.color }]}>
              <Text style={styles.achievementTagText}>{achievement.category}</Text>
            </View>
            <View style={[styles.achievementTag, { backgroundColor: getRarityColor(achievement.rarity) }]}>
              <Text style={styles.achievementTagText}>{achievement.rarity}</Text>
            </View>
          </View>
        </View>
      </View>
    );
  };

  const obtainedCount = userAchievements.length;
  const totalCount = allAchievements.length;

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.logo}>Movz</Text>
        <TouchableOpacity 
          style={styles.menuIcon} 
          onPress={() => setIsMenuVisible(true)}
        >
          <View style={styles.menuLine} />
          <View style={styles.menuLine} />
          <View style={styles.menuLine} />
        </TouchableOpacity>
      </View>

      {/* Title Section */}
      <View style={styles.titleSection}>
        <Text style={styles.title}>Conquistas</Text>
        <Text style={styles.subtitle}>
          {obtainedCount} de {totalCount} conquistas obtidas
        </Text>
        
        {/* Progress Bar */}
        <View style={styles.progressContainer}>
          <View style={styles.progressBar}>
            <View 
              style={[
                styles.progressFill, 
                { width: `${(obtainedCount / totalCount) * 100}%` }
              ]} 
            />
          </View>
          <Text style={styles.progressText}>
            {Math.round((obtainedCount / totalCount) * 100)}% completo
          </Text>
        </View>
      </View>

      {/* Achievements Grid */}
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <View style={styles.achievementsGrid}>
          {allAchievements.map(renderAchievementCard)}
        </View>
      </ScrollView>
      
      {/* Side Menu */}
      <SideMenu 
        isVisible={isMenuVisible} 
        onClose={() => setIsMenuVisible(false)}
        onNavigate={onNavigate}
        currentUser={currentUser}
        onLogout={onLogout}
        userType="STUDENT"
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E9EDEE',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 10,
  },
  logo: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
  },
  menuIcon: {
    width: 39,
    height: 18,
    justifyContent: 'space-between',
  },
  menuLine: {
    width: 39,
    height: 6,
    backgroundColor: '#D9D9D9',
    borderRadius: 3,
  },
  titleSection: {
    paddingHorizontal: 20,
    paddingBottom: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
    marginBottom: 5,
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    fontFamily: 'Poppins',
    marginBottom: 20,
  },
  progressContainer: {
    marginBottom: 10,
  },
  progressBar: {
    height: 8,
    backgroundColor: '#D9D9D9',
    borderRadius: 4,
    overflow: 'hidden',
    marginBottom: 8,
  },
  progressFill: {
    height: '100%',
    backgroundColor: '#F9BB55',
    borderRadius: 4,
  },
  progressText: {
    fontSize: 14,
    color: '#666',
    fontFamily: 'Poppins',
    textAlign: 'center',
  },
  scrollView: {
    flex: 1,
    paddingHorizontal: 20,
  },
  achievementsGrid: {
    paddingBottom: 20,
  },
  achievementCard: {
    backgroundColor: '#FFFFFF',
    borderRadius: 15,
    padding: 20,
    marginBottom: 15,
    flexDirection: 'row',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  achievementCardObtained: {
    borderWidth: 2,
    borderColor: '#F9BB55',
  },
  achievementIconContainer: {
    position: 'relative',
    marginRight: 15,
  },
  achievementIcon: {
    width: 60,
    height: 60,
  },
  achievementIconLocked: {
    opacity: 0.3,
  },
  obtainedBadge: {
    position: 'absolute',
    top: -5,
    right: -5,
    backgroundColor: '#2ECC71',
    borderRadius: 10,
    width: 20,
    height: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  obtainedText: {
    color: '#FFFFFF',
    fontSize: 12,
    fontWeight: 'bold',
  },
  achievementInfo: {
    flex: 1,
  },
  achievementName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#000',
    fontFamily: 'Poppins',
    marginBottom: 5,
  },
  achievementNameLocked: {
    color: '#999',
  },
  achievementDescription: {
    fontSize: 14,
    color: '#666',
    fontFamily: 'Poppins',
    marginBottom: 5,
  },
  achievementDescriptionLocked: {
    color: '#CCC',
  },
  achievementRequirement: {
    fontSize: 12,
    color: '#888',
    fontFamily: 'Poppins',
    marginBottom: 10,
  },
  achievementRequirementLocked: {
    color: '#DDD',
  },
  achievementTags: {
    flexDirection: 'row',
    gap: 8,
  },
  achievementTag: {
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 12,
  },
  achievementTagText: {
    fontSize: 10,
    color: '#FFFFFF',
    fontWeight: 'bold',
    fontFamily: 'Poppins',
  },
});

export default AchievementsScreen;
