import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Dimensions,
  Animated,
  Image,
} from 'react-native';

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');


const SideMenu = ({ isVisible, onClose, onNavigate, currentUser, onLogout, userType = 'STUDENT' }) => {
  const slideAnim = React.useRef(new Animated.Value(screenWidth)).current;

  React.useEffect(() => {
    if (isVisible) {
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 300,
        useNativeDriver: false,
      }).start();
    } else {
      Animated.timing(slideAnim, {
        toValue: screenWidth,
        duration: 300,
        useNativeDriver: false,
      }).start();
    }
  }, [isVisible, slideAnim]);

  if (!isVisible) return null;

  const getMenuItems = () => {
    if (userType === 'TEACHER') {
      return [
        { id: 'agenda', title: 'Agenda de Aulas', y: 200, height: 42, screen: 'teacherSchedule' },
        { id: 'classes', title: 'Minhas Aulas', y: 269, height: 42, screen: 'teacherClasses' },
        { id: 'myClasses', title: 'Minhas Turmas', y: 339, height: 42, screen: 'myClasses' },
        { id: 'perfil', title: 'Meus Dados', y: 408, height: 42, screen: 'teacherProfile' },
        { id: 'logout', title: 'Sair', y: 477, height: 42, screen: 'logout' },
      ];
    } else if (userType === 'INSTITUTION') {
      return [
        { id: 'logout', title: 'Sair', y: 200, height: 42, screen: 'logout' },
      ];
    } else {
      // STUDENT
      return [
        { id: 'perfil', title: 'Perfil', y: 200, height: 42, screen: 'home' },
        { id: 'myClass', title: 'Minha Turma', y: 269, height: 42, screen: 'myClass' },
        { id: 'ranking', title: 'Ranking', y: 339, height: 42, screen: 'ranking' },
        { id: 'chat', title: 'Chat', y: 408, height: 41, screen: 'chat' },
        { id: 'tutorial', title: 'Tutorial', y: 477, height: 42, screen: 'tutorial' },
        { id: 'logout', title: 'Sair', y: 546, height: 42, screen: 'logout' },
      ];
    }
  };

  const menuItems = getMenuItems();

  return (
    <View style={styles.overlay}>
      <TouchableOpacity style={styles.backdrop} onPress={onClose} />
      <Animated.View
        style={[
          styles.menuContainer,
          {
            transform: [{ translateX: slideAnim }],
          },
        ]}
      >
        {/* Main Background */}
        <View style={styles.background} />
        
        {/* Bottom Circle */}
        <View style={styles.bottomCircle} />
        
        {/* Close Button X in bottom circle */}
        <TouchableOpacity style={styles.bottomCloseButton} onPress={onClose}>
          <Text style={styles.closeX}>×</Text>
        </TouchableOpacity>

        {/* Top Close Button */}
        <TouchableOpacity style={styles.topCloseButton} onPress={onClose}>
          <Text style={styles.topCloseText}>×</Text>
        </TouchableOpacity>

        {/* User Avatar */}
        <View style={styles.avatarContainer}>
          <Image
            source={require('../assets/images/aiAtivo 1logo.png')}
            style={styles.userAvatar}
            resizeMode="cover"
          />
        </View>

        {/* Menu Items */}
        {menuItems.map((item, index) => (
          <TouchableOpacity 
            key={item.id} 
            style={[styles.menuItem, { top: item.y - 23 }]}
            onPress={() => {
              if (item.screen === 'logout') {
                onLogout && onLogout();
              } else {
                onNavigate && onNavigate(item.screen);
              }
            }}
          >
            <Text style={styles.menuItemText}>{item.title}</Text>
          </TouchableOpacity>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: 1000,
  },
  backdrop: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  // Background blur effect
  blurBackground: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.3)', // Semi-transparent overlay
    // Add blur effect using shadow
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.1,
    shadowRadius: 10,
    elevation: 5,
  },
  menuContainer: {
    position: 'absolute',
    top: 23, // Exact Figma y position
    right: 0,
    width: Math.min(195, screenWidth * 0.6), // Responsive width, max 60% of screen
    height: screenHeight - 23, // Full height minus top offset
    maxHeight: 584, // Max height from Figma
  },
  // Main background rectangle with orange color and drop shadow
  backgroundRectangle: {
    position: 'absolute',
    top: 0,
    left: 27, // x: 235 - 208 = 27
    width: Math.min(168, screenWidth * 0.5), // Responsive width
    height: '100%', // Full height of container
    backgroundColor: 'rgba(241, 147, 65, 0.9)', // 90% opacity
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 0,
    },
    shadowOpacity: 0.25, // Exact Figma opacity
    shadowRadius: 7.4, // Exact Figma radius
    elevation: 8, // Android shadow
    zIndex: 1, // Lower z-index than circle
  },
  // Noise overlay for texture effect
  noiseOverlay: {
    position: 'absolute',
    top: 0,
    left: 27,
    width: Math.min(168, screenWidth * 0.5), // Responsive width
    height: '100%', // Full height of container
    backgroundColor: 'transparent',
    // Create noise effect using multiple pseudo-elements
    opacity: 0.1,
    // Add subtle pattern using border
    borderWidth: 0.5,
    borderColor: 'rgba(255, 255, 255, 0.1)',
    // Add subtle shadow for depth
    shadowColor: 'rgba(0, 0, 0, 0.1)',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    zIndex: 2, // Above rectangle but below circle
  },
  // Bottom circle element
  bottomCircle: {
    position: 'absolute',
    bottom: 30, // Responsive positioning from bottom
    left: 0, // x: 208 - 208 = 0
    width: 56, // Exact Figma width
    height: 54, // Exact Figma height
    borderRadius: 28, // Half of width for perfect circle
    backgroundColor: 'rgba(241, 147, 65, 0.9)', // Same orange color with 90% opacity
    // Add subtle border for texture
    borderWidth: 0.5,
    borderColor: 'rgba(255, 255, 255, 0.1)',
    zIndex: 3, // Above rectangle and noise overlay
    elevation: 9, // Higher elevation for Android
  },
  // Close button in bottom circle - perfectly centered
  bottomCloseButton: {
    position: 'absolute',
    bottom: 39, // Responsive: 30 + (54/2) - (24/2) = 39
    left: 16, // Perfect center: 0 + (56/2) - (24/2) = 16
    width: 24,
    height: 24,
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 4, // Above circle
  },
  bottomCloseIcon: {
    width: 18, // Perfect size for the circle
    height: 18,
    justifyContent: 'center',
    alignItems: 'center',
  },
  // Top close button (X)
  topCloseButton: {
    position: 'absolute',
    top: 10,
    right: 10,
    width: 30,
    height: 30,
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 10,
  },
  topCloseText: {
    fontSize: 24,
    color: '#fff',
    fontWeight: 'bold',
  },
  // Close button lines
  closeLine: {
    position: 'absolute',
    width: 14, // Perfect size for 18px icon
    height: 2, // Perfect thickness
    backgroundColor: '#fff', // Exact Figma color
  },
  closeLine1: {
    transform: [{ rotate: '45deg' }],
  },
  closeLine2: {
    transform: [{ rotate: '-45deg' }],
  },
  // Avatar container
  avatarContainer: {
    position: 'absolute',
    top: 42, // y: 65 - 23 = 42
    left: 80, // x: 288 - 208 = 80, centered in the rectangle
    width: 61, // Exact Figma width
    height: 60, // Exact Figma height
  },
  userAvatar: {
    width: 61, // Exact Figma width
    height: 60, // Exact Figma height
    borderRadius: 30.5, // Half of width for perfect circle
  },
  // Menu items
  menuItem: {
    position: 'absolute',
    left: 27, // Same as background rectangle
    width: 168, // Same as background rectangle
    height: 42, // Standard height
    justifyContent: 'center',
    alignItems: 'center',
  },
  menuItemText: {
    fontSize: 20,
    fontWeight: '500',
    color: '#fff', // Exact Figma color
    textAlign: 'center',
  },
});

export default SideMenu;
