// Script para testar o frontend com debug
const fetch = require('node-fetch');

async function testFrontendDebug() {
  console.log('ðŸ§ª Testando frontend com debug...\n');

  // Simular dados do formulÃ¡rio
  const formData = {
    name: 'Teste Frontend Debug',
    email: 'debug@teste.com',
    password: '123456',
    age: '25', // String como vem do input
    school: 'Escola Debug',
    class: 'Turma Debug'
  };

  console.log('ðŸ“ Dados do formulÃ¡rio (como vem do input):', formData);

  // Converter age para nÃºmero (como fazemos no frontend)
  const userDataToSend = {
    ...formData,
    age: parseInt(formData.age, 10)
  };

  console.log('ðŸ“¤ Dados a serem enviados (apÃ³s conversÃ£o):', userDataToSend);

  try {
    console.log('ðŸ“¡ Enviando para API...');
    
    const response = await fetch('http://localhost:3001/api/auth/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userDataToSend),
    });

    console.log('ðŸ“¡ Status:', response.status);
    const data = await response.json();
    console.log('ðŸ“¡ Resposta:', data);

    if (response.ok && data.success) {
      console.log('âœ… Teste bem-sucedido!');
      console.log('ðŸ‘¤ UsuÃ¡rio criado:', data.data.user.name);
      console.log('ðŸ“§ Email:', data.data.user.email);
      console.log('ðŸŽ¯ PrÃ³ximo passo: Testar no navegador');
    } else {
      console.log('âŒ Erro na API:', data.message);
    }

  } catch (error) {
    console.error('âŒ Erro na requisiÃ§Ã£o:', error.message);
  }
}

testFrontendDebug();
