import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
  Image,
  Dimensions,
} from 'react-native';
import apiService from './apiService';

const { width } = Dimensions.get('window');

const ClassScreen = ({ isMenuVisible, setIsMenuVisible, onNavigate, currentUser, onLogout }) => {
  // Usar par√¢metros passados via navega√ß√£o
  const classData = window.navigationParams?.classData || {};

  const [attendanceTaken, setAttendanceTaken] = useState(false);
  const [classCompleted, setClassCompleted] = useState(false);
  const [loading, setLoading] = useState(false);
  const [expandedCard, setExpandedCard] = useState('aquecimento');
  const [showCompletionModal, setShowCompletionModal] = useState(false);

  // Dados de exemplo dos exerc√≠cios (em produ√ß√£o viria da API)
  const workoutSections = [
    {
      id: 'aquecimento',
      title: 'Aquecimento',
      duration: '7min',
      exercises: [
        { name: 'Corrida Leve', repetitions: '5 minutos' },
        { name: 'Alongamento Din√¢mico', repetitions: '2 minutos' },
        { name: 'Aquecimento Articular', repetitions: '1 s√©rie' },
      ],
    },
    {
      id: 'treino',
      title: 'Treino',
      duration: '30min',
      exercises: [
        { name: 'Saque', repetitions: '20 repeti√ß√µes' },
        { name: 'Recep√ß√£o', repetitions: '20 repeti√ß√µes' },
        { name: 'Levantamento', repetitions: '20 repeti√ß√µes' },
        { name: 'Ataque', repetitions: '20 repeti√ß√µes' },
        { name: 'Bloqueio', repetitions: '20 repeti√ß√µes' },
        { name: 'Defesa', repetitions: '20 repeti√ß√µes' },
      ],
    },
    {
      id: 'desaquecimento',
      title: 'Desaquecimento',
      duration: '8min',
      exercises: [
        { name: 'Alongamento Final', repetitions: '5 minutos' },
        { name: 'Relaxamento', repetitions: '3 minutos' },
      ],
    },
  ];

  const handleTakeAttendance = async () => {
    try {
      setLoading(true);
      // Aqui seria implementada a l√≥gica de chamada
      // Por enquanto, apenas simula o processo
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setAttendanceTaken(true);
      Alert.alert('Sucesso', 'Chamada realizada com sucesso!');
    } catch (error) {
      Alert.alert('Erro', 'Erro ao realizar a chamada');
    } finally {
      setLoading(false);
    }
  };

  const handleCompleteClass = async () => {
    try {
      setLoading(true);
      // Aqui seria implementada a l√≥gica de conclus√£o da aula
      // Por enquanto, apenas simula o processo
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setClassCompleted(true);
      Alert.alert('Sucesso', 'Aula conclu√≠da com sucesso!');
      
      // Navegar de volta para a lista de aulas
      onNavigate('classes');
    } catch (error) {
      Alert.alert('Erro', 'Erro ao concluir a aula');
    } finally {
      setLoading(false);
    }
  };

  const renderExerciseCard = (exercise) => (
    <View key={exercise.id} style={styles.exerciseCard}>
      <Text style={styles.exerciseName}>{exercise.name}</Text>
      <Text style={styles.exerciseReps}>{exercise.reps}</Text>
    </View>
  );

  const renderExerciseSection = (title, exercises, icon) => (
    <View style={styles.exerciseSection}>
      <View style={styles.sectionHeader}>
        <View style={styles.sectionIcon}>
          <Text style={styles.sectionIconText}>{icon}</Text>
        </View>
        <Text style={styles.sectionTitle}>{title}</Text>
      </View>
      <View style={styles.exercisesGrid}>
        {exercises.map(renderExerciseCard)}
      </View>
    </View>
  );

  return (
    <ScrollView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <View style={styles.headerTop}>
          <Text style={styles.logo}>Muvz</Text>
          <View style={styles.menuLines}>
            <View style={styles.menuLine} />
            <View style={styles.menuLine} />
            <View style={styles.menuLine} />
          </View>
        </View>
        
        <Text style={styles.title}>In√≠cio da Aula</Text>
        <Text style={styles.subtitle}>
          Vamos come√ßar a aula da {classData?.class || '5¬™ s√©rie'}?
        </Text>
        
        <View style={styles.divider} />
        
        <Text style={styles.classTitle}>{classData?.sport || 'Treino de V√¥lei'}</Text>
        <Text style={styles.classInfo}>
          Dura√ß√£o: {classData?.duration || '60min'} Esse treino vale {classData?.xp || '60'}xp para seus alunos
        </Text>
      </View>

      {/* Bot√£o de Chamada */}
      <TouchableOpacity 
        style={[styles.attendanceButton, attendanceTaken && styles.attendanceButtonCompleted]}
        onPress={handleTakeAttendance}
        disabled={attendanceTaken || loading}
      >
        <Text style={styles.attendanceButtonText}>
          {attendanceTaken ? 'Chamada Realizada' : 'Fazer a Chamada'}
        </Text>
      </TouchableOpacity>

      {/* Se√ß√µes de Exerc√≠cios */}
      {renderExerciseSection('Aquecimento', exercises.warmup, 'üî•')}
      {renderExerciseSection('Treino', exercises.main, '‚öΩ')}
      {renderExerciseSection('Desaquecimento', exercises.cooldown, '‚ùÑÔ∏è')}

      {/* Bot√£o de Conclus√£o */}
      <TouchableOpacity 
        style={[styles.completeButton, classCompleted && styles.completeButtonCompleted]}
        onPress={handleCompleteClass}
        disabled={classCompleted || loading}
      >
        <Text style={styles.completeButtonText}>
          {classCompleted ? 'Aula Conclu√≠da' : 'Come√ßar'}
        </Text>
      </TouchableOpacity>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EDED',
  },
  header: {
    padding: 20,
    paddingTop: 40,
  },
  headerTop: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  logo: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#000',
  },
  menuLines: {
    flexDirection: 'column',
    gap: 3,
  },
  menuLine: {
    width: 39,
    height: 6,
    backgroundColor: '#D9D9D9',
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 18,
    color: '#000',
    marginBottom: 20,
  },
  divider: {
    height: 1,
    backgroundColor: '#000',
    marginBottom: 20,
  },
  classTitle: {
    fontSize: 36,
    fontWeight: 'bold',
    color: '#000',
    textAlign: 'center',
    marginBottom: 10,
  },
  classInfo: {
    fontSize: 18,
    color: '#000',
    textAlign: 'center',
    marginBottom: 20,
  },
  attendanceButton: {
    backgroundColor: '#B5B5B5',
    marginHorizontal: 20,
    marginBottom: 20,
    paddingVertical: 15,
    borderRadius: 10,
    alignItems: 'center',
  },
  attendanceButtonCompleted: {
    backgroundColor: '#4CAF50',
  },
  attendanceButtonText: {
    fontSize: 18,
    fontWeight: '600',
    color: '#000',
  },
  exerciseSection: {
    backgroundColor: '#fff',
    marginHorizontal: 20,
    marginBottom: 20,
    paddingVertical: 15,
  },
  sectionHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 20,
    marginBottom: 15,
  },
  sectionIcon: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: '#F9BB55',
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 15,
  },
  sectionIconText: {
    fontSize: 16,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#000',
  },
  exercisesGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    paddingHorizontal: 20,
    gap: 10,
  },
  exerciseCard: {
    width: (width - 80) / 3,
    height: 91,
    backgroundColor: '#F9BB55',
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
  },
  exerciseName: {
    fontSize: 12,
    fontWeight: '600',
    color: '#000',
    textAlign: 'center',
    marginBottom: 5,
  },
  exerciseReps: {
    fontSize: 12,
    color: '#000',
    textAlign: 'center',
  },
  completeButton: {
    backgroundColor: '#B5B5B5',
    marginHorizontal: 20,
    marginBottom: 40,
    paddingVertical: 15,
    borderRadius: 10,
    alignItems: 'center',
  },
  completeButtonCompleted: {
    backgroundColor: '#4CAF50',
  },
  completeButtonText: {
    fontSize: 18,
    fontWeight: '600',
    color: '#000',
  },
});

export default ClassScreen;
