    try {
      setIsLoading(true);
      const response = await apiService.getSports();
      if (response.success) {
        setSports(response.data);
      } else {
        Alert.alert('Erro', 'N√£o foi poss√≠vel carregar os esportes');
      }
    } catch (error) {
      console.error('Erro ao carregar esportes:', error);
      Alert.alert('Erro', 'Erro ao carregar esportes');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSportSelection = (sport) => {
    setSelectedSport(sport);
    setCurrentStep('workout');
    setWorkoutProgress(0);
    setCurrentWorkoutStep(0);
  };

  const handleWorkoutComplete = async () => {
    try {
      // Aqui voc√™ pode implementar a l√≥gica para salvar o progresso do treino
      // Por exemplo, incrementar o contador de treinos realizados
      Alert.alert(
        'Parab√©ns! üéâ',
        `Voc√™ completou o treino de ${selectedSport.name}!`,
        [
          {
            text: 'OK',
            onPress: () => {
              setCurrentStep('sport-selection');
              setSelectedSport(null);
              setWorkoutProgress(0);
              setCurrentWorkoutStep(0);
            }
          }
        ]
      );
    } catch (error) {
      console.error('Erro ao salvar progresso:', error);
      Alert.alert('Erro', 'Erro ao salvar progresso do treino');
    }
  };

  const handleWorkoutStepComplete = () => {
    const totalSteps = selectedSport ? workoutData[selectedSport.name]?.sections.length || 0 : 0;
    const newStep = currentWorkoutStep + 1;
    
    if (newStep >= totalSteps) {
      handleWorkoutComplete();
    } else {
      setCurrentWorkoutStep(newStep);
      setWorkoutProgress((newStep / totalSteps) * 100);
    }
  };

